 {
	"action_insert": "false",
	"action_update": "true",
	"action_delete": "false",
	"action_query": "false",
	"collection": "task",
	"advanced": "true",
	"active": "true",
	"when": "async",
	"order": "200",
	"add_message": "false",
	"script": "if (current != null) {
	var thisTask = current.sys_id;
	var noNotify = {};
	var visited = {};
	var maxLevel = parseInt(gs.getProperty('NotifyAffectedCI.max_rel_level'));
	var maxCis = parseInt(gs.getProperty('NotifyAffectedCI.max_cis', '100000'));
	// max_events determines how many cis may be tracked in the noNotify array
	// increasing to a very large number ( > ~100K) might use too much memory
	var maxEvents = parseInt(gs.getProperty('NotifyAffectedCI.max_events', '50000'));
	var legacySubscriptionModel = gs.getProperty('glide.notification.use_legacy_subscription_model');
	// Need to limit size of the array in memory used to track visited CIs
	var maxArrSize = parseInt(gs.getProperty('NotifyAffectedCI.max_visited_arr_size', '50000'));

	var eventCount = 0;
	var ciAffectedNotificationTypes = getCIAffectedNotificationTypes();
	var ciCount = 0;
	var ciBatchSize = 100;

	var affectedItem = new GlideRecord('task_ci');
	affectedItem.addQuery('task', thisTask);
	affectedItem.query();
	var nextLevel = [];
	while (affectedItem.next()) {
		ciCount++;

		if (maxCisReached())
			break;

		var itemSysID = affectedItem.ci_item + '';
		if (!visited[itemSysID]) {
			nextLevel.push(itemSysID);
			markAsVisited(itemSysID);
		}
	}
	// send notifications for subscribed cis in nextlevel array
	notifySubscribers(nextLevel);
	// walk the relationships of nextLevel Cis and notify
	if (!maxCisReached())
		notifyParentsByLevel(nextLevel, 1);

}


function notifyParentsByLevel(cis, currentLevel) {
	if (currentLevel > maxLevel || cis.length < 1) return;

	for (var index = 0; index < cis.length; index += ciBatchSize) {
		var nextCIs = [];
		if (eventCount >= maxEvents) break;

		var batch = cis.slice(index, index + ciBatchSize);

		var rel = new GlideRecord('cmdb_rel_ci');
		rel.addQuery('child', 'IN', batch);
		rel.addNotNullQuery('type');
		rel.addNotNullQuery('parent');
		rel.query();
		while (rel.next()) {
			ciCount++;

			if (maxCisReached())
				break;

			var parentSysID = rel.parent + '';
			if (!visited[parentSysID]) {
				nextCIs.push(parentSysID);
				markAsVisited(parentSysID);
			}
		}
		notifySubscribers(nextCIs);

		if(nextCIs.length > 0 && (currentLevel + 1) <= maxLevel && !maxCisReached())
			notifyParentsByLevel(nextCIs, currentLevel + 1);
	}


}

function notifySubscribers(ciSysIds) {
	if (ciSysIds.length == 0)
		return;

	var ciList = [];
	for (var index = 0; index < ciSysIds.length; index += ciBatchSize) {
		var batch = ciSysIds.slice(index, index + ciBatchSize);

		ciList = [];
		if(legacySubscriptionModel == 'true') {
			var mGr = new GlideAggregate('cmn_notif_message');
			mGr.addQuery('configuration_item', 'IN', batch);
			mGr.addQuery('notification', 'IN', ciAffectedNotificationTypes);
			mGr.groupBy('configuration_item');
			mGr.query();
			while (mGr.next()) {
				var ci = mGr.configuration_item + '';
				ciList.push(ci);
			}
		}
		else {
			var nGr = new GlideAggregate('sys_notif_subscription');
			nGr.addActiveQuery();
			nGr.addQuery('affected_record', 'IN', batch);
			nGr.addQuery('notification', 'IN', ciAffectedNotificationTypes);
			nGr.groupBy('affected_record');
			nGr.query();
			while (nGr.next()) {
				var ar = nGr.affected_record + '';
				ciList.push(ar);
			}
		}
		if (ciList.length > 0)
						queueCis(ciList);
	}

}

function queueCis(ciList) {
	var ciDisplayName = '', ciSysId = '';
	var ciGr = new GlideRecord('cmdb_ci');
	ciGr.addQuery('sys_id', 'IN', ciList);
	ciGr.query();
	while (ciGr.next()){
		ciSysId = ciGr.sys_id;
		ciDisplayName = ciGr.getDisplayValue();
		if (!noNotify[ciSysId]) {
			gs.eventQueue('ci.affected', current, ciSysId, ciDisplayName, 'cmdb_ci_affected');
			markAsNotified(ciSysId);
			eventCount++;
			if (eventCount >= maxEvents) return;
		}
	}
}

function getCIAffectedNotificationTypes() {
	var gr = new GlideRecord('sysevent_email_action');
	gr.addQuery('event_name', 'ci.affected');
	gr.addActiveQuery();
	gr.query();
	var ids = '';
	while (gr.next()) {
		ids += gr.getValue('sys_id');
		if(gr.hasNext())
			ids += ',';
	}

	return ids;
}

function markAsVisited(ci) {
	var size = Object.keys(visited).length;
	if (size < maxArrSize)
		visited[ci] = true;
}

function markAsNotified(ci) {
	if (visited[ci])
		return; // ci will not be visited twice so need to track

	noNotify[ci] = true;
}

function maxCisReached() {
	if (isNaN(maxCis))
		return false;
	return ciCount > maxCis;
}",
