		};

		var allowSettingState = additionalInfo.sn_allow_setting_state;
		if (allowSettingState == USER_CAN_SET_STATE.NONE) {
				// "None" means this is a L3 alert that cannot be opened or closed directly by users, so this business rule has nothing to do here.
				return;
		}

		// Assumption: there is a previous business rule that ensured that we are allowed to change the state of this alert,
		// so no further validation need be done here. We are just going to close/reopen related alerts as necessary.
		var parentGR = current.parent.getRefRecord();
		if (current.state == "Closed") {
				// 1. Close all my children
				updateAllChildren(current, "Closed");

				// 2. If I was the last open child of my parent, close the parent.
				var openSiblings = new GlideRecord("em_alert");
				openSiblings.addQuery("parent", current.parent);
				openSiblings.addQuery("state", "!=", "Closed");
				openSiblings.query();
				var openCount = openSiblings.getRowCount();
				if (openCount == 0) {
						parentGR.state = "Closed";
						parentGR.update();
				}
		} else { // reopen
				if (allowSettingState == USER_CAN_SET_STATE.CLOSE) {
						// this is an alert that could not have been reopened directly, so nothing to do here.
						return;
				}
				// 1. Reopen my parent
				if (parentGR.state == "Closed") {
						parentGR.state = "Reopen";
						parentGR.update();
				}

				// 2. Reopen all my children
				updateAllChildren(current, "Reopen");
		}

		function updateAllChildren(alert, state) {
				var childGR = new GlideRecord("em_alert");
				childGR.addQuery("parent", alert.sys_id);
				childGR.setValue("state", state);
				childGR.updateMultiple(); // this will trigger this same rule in turn on all children
		}

})(current, previous);",
