		//update predictions for current case
		while (predictions.next()) {
				var predictionField = predictions.getValue('predicted_output_value_name');
				var actualFieldValue = current.getDisplayValue(predictionField);
				var predictedValue = getPredictedValue(predictions);

				if (actualFieldValue == predictedValue) {
						predictions.setValue('predicted_correctly', true);
						predictions.update();
				}
		}

		//update HR service predictions for transferred-from case
		if (updatePredictionForTransferredFromCase) {
				var hrServicePredictions = getPredictionGR(current.getValue('transferred_from'), true);

				while (hrServicePredictions.next()) {
						var actualHRService = current.getDisplayValue('hr_service');
						var predictedHRService = getPredictedValue(hrServicePredictions);

						if (actualHRService == predictedHRService) {
								hrServicePredictions.setValue('predicted_correctly', true);
								hrServicePredictions.setValue("final_value_date", new GlideDateTime());
								hrServicePredictions.setValue("final_output_value", actualHRService);

								hrServicePredictions.update();
						}
				}
		}

		function getPredictionGR(sourceCaseID, forHRService) {
				var gr = new GlideRecord('ml_predictor_results');
				gr.addQuery('source_sys_id', sourceCaseID);
				gr.addQuery('predicted_correctly', false);
