	"script": "(function () {
	var tag_pattern = "/<sys_id(.*?)>(.*?)<\/sys_id>/";
	var state_pattern = "/\s*state\s*=\s*\"(.*?)\"/";
	var reason_pattern = "/\s*reason\s*=\s*\"(.*?)\"/";
	var regex = new SNC.Regex(tag_pattern);
	var state_regex = new SNC.Regex(state_pattern);
	var reason_regex = new SNC.Regex(reason_pattern);
	var match = regex.match(current.payload);
	while (match) {
		var attributes = match[1];
		var id = match[2];
		var state = 'processing', reason = null;
		var state_match = state_regex.match(attributes);
		if (state_match && state_match[1])
			state = state_match[1];

		var reason_match = reason_regex.match(attributes);
		if (reason_match && reason_match[1])
			reason = reason_match[1];

		markComplete(id, state, reason);
		match = regex.match();
	}
})();
current.state = 'processed';
current.processed = gs.nowDateTime();

function markComplete(id, state, reason) {
	var tableName = 'ecc_queue';
	var gr = new GlideRecord(tableName);
	if (!gr.get(id))
		return;

	if (state == 'processing') {
		// get the real rotated table name: mutli-update does not take care of table rotation
		var sys_table_name = gr.getValue('sys_table_name');
		// if table is not rotated, use the original table name
		if (JSUtil.nil(sys_table_name))
			sys_table_name = tableName;

		// use multi-update to perform atomic update with a where clause to avoid race condition
		var mu = GlideMultipleUpdate(sys_table_name);
		mu.addQuery('sys_id', id);
		mu.addQuery('state', 'ready');
		mu.setValue('state', state);
		if (reason)
			mu.setValue('error_string', reason);

		// When setting the 'processed' field using GlideMultipleUpdate.setValue(String)
		// with gs.nowDateTime() will not work correctly.
		// We need to provide now as a UTC value because when the String provided to
		// GlideMultipleUpdate.setValue(String) gets parsed it will be interpreted it as UTC.
		mu.setValue('processed', gs.nowNoTZ());
		mu.execute();
	} else {
		gr.state = state;
		if (reason)
			gr.error_string = reason;

		gr.processed = gs.nowDateTime();
		gr.update();
	}
}
",
	"abort_action": "false",
	"filter_condition": "",
	"condition": "current.topic == "queue.processing"",
	"name": "ECC Queue - mark outputs state"
}```

