function sys_cb_decision_variableGetChoices() {

	// if we don't have current, we can't figure out the variable table
	if (typeof current == "undefined" || JSUtil.nil(current) || !current || JSUtil.nil(current.sys_id)) {
		gs.log("Error generating Topic choice-variable choices: current record is undefined or empty");
		return;
