	//answer should be automatically defined in our context when this function is called
	if (!answer) {
		gs.log("Error generating Topic choice-variable choices: answer is undefined");
		return;
	}
	var variable_table = current.getValue("variable_table");
	var variable_lookup = new TopicVariableAsChoice();

	var choices = variable_lookup.getChoices(variable_table);

	for (var i=0; i< choices.length; i++) {
		var choice = choices[i];
		answer.add(choice.value, choice.label);
	}	
