	var map = {};
	var conflictGr = new GlideRecord('conflict');
	conflictGr.addQuery('change', current.getUniqueValue()).addOrCondition('conflicting_change', current.getUniqueValue());
	conflictGr.query();	
	while (conflictGr.next()) {
		map[conflictGr.change.toString()] = null;
		map[conflictGr.conflicting_change.toString()] = null;					
		conflictGr.deleteRecord();
	}
	delete map[current.getUniqueValue()];

	// Update conflict fields for associated change_request records
	for (var sysId in map) {
		conflictGr = new GlideRecord('conflict');
		conflictGr.addQuery('change', sysId).addOrCondition('conflicting_change', sysId);
		conflictGr.query();
