						connectorVal.addQuery("name", "scom_version");
						connectorVal.query();
						var isDefined = 0;
						if (connectorVal.next()) {
								if (connectorVal.getValue("value") === "2007" || connectorVal.getValue("value") === "2012" || connectorVal.getValue("value") === "2016" || connectorVal.getValue("value") === "2019")
										isDefined = 1;
						}
						if (isDefined == 0)
								errorAndAbort("SCOM version is not defined or invalid");
				}
		}

		//Validation of connector instance name to be unique. STRY53357484
		//if its insert, then dont allow connector instance with same name as active connector instance name.
		if (current.operation() == "insert") {
				var connectorInstances = new GlideRecord("em_connector_instance");
				connectorInstances.addQuery("name", current.name);
				//If a new connector instance is getting created, dont allow to create with same name eventhough they are not active
				//connectorInstances.addActiveQuery() 
				connectorInstances.query();
				if (connectorInstances.next()) {
						errorAndAbort("Connector Instance with same name already exists. Specify different name.");
				}

		} else if (current.operation() == "update") {
				//for existing records if user is changing some info in the connector instance, we cannot restrict the user to use different name.
				var connectorInstances = new GlideRecord("em_connector_instance");
				connectorInstances.addQuery("name", current.name);
				connectorInstances.addQuery("sys_id", "!=", current.sys_id);
				connectorInstances.addActiveQuery();
				connectorInstances.query();
				if (connectorInstances.next()) {
						if (current.name == previous.name) {
								//for existing records if user is changing some info in the connector instance otherthan name, we cannot restrict the user to use different name.
								gs.addInfoMessage(gs.getMessage("Connector Instance with same name '{0}' already exists. It is recommended to use unique name.",current.name));
						} else {
								//if user is changing the name of the existing connector with the same name of another existing connector instance, error out.
								errorAndAbort("Connector Instance with same name already exists. Specify different name.");
						}
				}

		}

	function errorAndAbort(msg) {
		var errMsg= gs.getMessage(msg);
		gs.addErrorMessage(errMsg);
		current.setAbortAction(true);
	}
