		g_attachment_dest_metadata = {
			table_name: tableNameNode.nodeValue,
			record_sysid: recordSysidNode.nodeValue,
			file_name: fileNameNode.nodeValue,
			encrypt_context_sysid: encryptCtxSysidNode.nodeValue
		};
	}

}

