	/*
	Status - 1 :Active
	2 - Inactive
	3 - Expired
	*/

	if(current.effective_immediately == true){
		current.effective_on = "";
	}
	var now = new GlideDateTime().getValue();

	//Mark active if effective immediately is true and now < expiry
	if(current.getValue("effective_immediately") == true && now < current.expires_on){
		 current.status = 1;
	 }else if(now >= current.expires_on){
		 current.status = 3;
	 }else if (current.getValue("effective_immediately") == false && now < current.effective_on){
		 //Mark inactive if now < effective on
		 current.status = 2;
	 }else if(current.getValue("effective_immediately") == false && current.effective_on <= now){
		 //Mark active if effective on < now
		 current.status = 1;
	 }


})(current, previous);","abort_action":"false","filter_condition":"effective_immediatelyVALCHANGES^OReffective_onVALCHANGES^ORexpires_onVALCHANGES^EQ","condition":"","name":"Setting state based on dates"}

 {"action_insert":"false","action_update":"false","action_delete":"true","action_query":"false","collection":"sys_db_view","advanced":"true","active":"true","when":"after","order":"100","add_message":"false","script":"// prevent transaction cancellation by quota rules
var t = null;
var isUncancelable = false;
try {
	// store original uncancelable state
	t = GlideTransaction.get();
	if (t != null) {
		isUncancelable = t.isUncancelable();
		t.setCannotCancel(true);
	}

	gs.print('Removing dictionary entries for ' + current.name);
	var dict = new GlideRecord('sys_dictionary');
	dict.initialize();
	dict.setWorkflow(true);
	dict.addQuery('name', current.name);
	dict.query();
	dict.deleteMultiple();

} finally {
	// restore original uncancelable state
