			gp.initialize();
			gp.name='change_phase';
			gp.element='name';
			gp.value=current.value;
			gp.label=current.label;
			gp.insert();
	 }
}

function deleteChangePhase(me) {
	 var gc = new GlideRecord('sys_choice');
	 gc.addQuery('name', '=', 'change_request');
	 gc.addQuery('element', '=', 'phase');
