	 gc.query();
	 if (!gc.next()) {
			var gp = new GlideRecord('sys_choice');
			gp.addQuery('name', '=', 'change_phase');
			gp.addQuery('element', '=', 'name');
			gp.addQuery('value', '=', me.value);
			gp.query();
			if (gp.next())
				 gp.deleteRecord();
	 }
}","abort_action":"false","filter_condition":"","condition":"","name":"Choice Events"}

