 {"action_insert":"true","action_update":"true","action_delete":"false","action_query":"false","collection":"item_option_new","advanced":"true","active":"true","when":"before","order":"100","add_message":"false","script":"(function executeRule(current, previous /*null when async*/) {
	var variableTypes = {
				'12' : 'Break',
				'14' : 'Macro',
				'15' : 'UI Page',
				'17' : 'Macro with Label',
				'19' : 'Container Start',
				'20' : 'Container End',
				'21' : 'List Collector',
				'23' : 'HTML',
				'24' : 'Container Split',
				'28' : 'IP',
				};

	var type = current.type;
	var width = current.variable_width;
	var msgArray = [variableTypes[type], width];

	if (type == '14' || type == '15' || type == '17' || type == '21' || type == '23') {
		if (width == '25')
			invalidWidthBlocker();
	}
	else if (type == '28' && (width == '75' || width == '100' ))
		invalidWidthBlocker();

	else if (type == '12' || type == '19' || type == '20' || type == '24') {
		if (width != '') 
			invalidWidthBlocker();
	}

	function invalidWidthBlocker() {
		var msg = gs.getMessage("{1}% width option not supported for {0} variable", msgArray);
		gs.addErrorMessage(msg);
		current.setAbortAction(true);
	}

	})(current, previous);","abort_action":"false","filter_condition":"","condition":"","name":"Validate Variable Width"}

 {"action_insert":"true","action_update":"true","action_delete":"false","action_query":"false","collection":"sn_publications_publication","advanced":"true","active":"true","when":"before","order":"200","add_message":"false","script":"(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	if((!current.publish_date.nil()) && (!current.expiry_date.nil()) && (current.stage=='review' || (current.stage=='author' && previous.stage!='review'))){
		var now = new GlideDateTime().getNumericValue();
		var start = (new GlideDateTime(current.getValue('publish_date'))).getNumericValue();	
		var end = (new GlideDateTime(current.getValue('expiry_date'))).getNumericValue();
		if (now > start) {
			gs.addErrorMessage(gs.getMessage('Publish date must be after current time'));
			current.setAbortAction(true);
		}

		if (start >= end) {
			gs.addErrorMessage(gs.getMessage('Publish date must be before Expiry date'));
			current.setAbortAction(true);
		}

		//compare with recurrence start/end date
		if (!current.recurrence.nil()){
			var gr = new GlideRecord('sn_publications_recurrence');
			if(gr.get(current.recurrence)){
				var rec_start = new GlideDateTime(gr.getValue('recurrence_start'));
				var rec_end = new GlideDateTime(gr.getValue('recurrence_end'));

				if(start < rec_start.getNumericValue() || end > rec_end.getNumericValue()){
					var linkTxt = gs.getMessage("recurrence");
					var link = "<b><a href='/sn_publications_recurrence.do?sys_id="+current.recurrence+"'>"+linkTxt+"</a></b>";
					var warnMsg = gs.getMessage("This publication is associated with this {0} between From: {1}	To : {2}",[link,rec_start.getDisplayValue(),rec_end.getDisplayValueInternal()]);
					gs.addErrorMessage(warnMsg);
					current.setAbortAction(true);
				}

			}

		}

	}
})(current, previous);","abort_action":"false","filter_condition":"","condition":"","name":"Date validation"}

