 {"action_insert":"true","action_update":"true","action_delete":"false","action_query":"false","collection":"sn_hr_core_document_template","advanced":"true","active":"true","when":"before","order":"100","add_message":"false","script":"(function executeRule(current, previous /*null when async*/) {
