 {"action_insert":"true","action_update":"true","action_delete":"false","action_query":"false","collection":"sys_email_system_filter","advanced":"true","active":"true","when":"after","order":"100","add_message":"false","script":"(function executeRule(current, previous /*null when async*/ ) {
