 {"action_insert":"true","action_update":"false","action_delete":"true","action_query":"false","collection":"clm_m2m_contract_asset","advanced":"true","active":"true","when":"after","order":"100","add_message":"false","script":"this.calcCostPerAsset(current.contract);

function calcCostPerAsset(contract) {
	if (contract.sys_id) {
		var gr = new GlideRecord('ast_contract');
		gr.get(contract.sys_id);
		if (gr.isValidRecord()) {
			var assets = new GlideRecord('clm_m2m_contract_asset');
			assets.addQuery('contract', contract.sys_id);
			assets.query();
			var count = 0;
			while (assets.next())
				count++;
			var average = 0;
			if (count > 0)
				average = gr.total_cost/count;
			gr.cost_per_unit = average;
			gr.update();
		}
	}
}","abort_action":"false","filter_condition":"","condition":"true","name":"Update contract cost per asset"}

