 {"action_insert":"true","action_update":"false","action_delete":"false","action_query":"false","collection":"ecc_queue","advanced":"true","active":"true","when":"before","order":"80","add_message":"false","script":"/**
 * Service-now.com
 * 
 * Description: If the MID server is in a cluster, potentially replace the agent with the proper one.
 */

runMIDManagement();

function runMIDManagement() {
		var agentGr = getMIDServerGr();
		if (JSUtil.nil(agentGr))
				return;

	// only use cluster for Shazzam if the MID is down
	if (current.topic == "Shazzam" && agentGr.status == "Up") 
		return;

	// If probe param ECC_AGENT_SELECTOR_DETAILS exists in payload, then exit and don't use
	// legacy cluster management. For performance, uses a cached regex instead of a real XML parser.
	var midSelectorRegex = new SNC.Regex(GlideappIECC.ECC_AGENT_SELECTOR_DETAILS_REGEX+'');
	if (midSelectorRegex.match(current.payload))
		return;

	// only if a mid is down should a failover cluster be considered
	var msc;
	if (agentGr.status == "Down" || agentGr.status == "Upgrade Failed")
		msc = new MIDServerCluster(agentGr, "Failover");
	else
			msc = new MIDServerCluster(agentGr, "Load Balance");

		if (!msc.clusterExists())	// no available clusters of any kind
				return;

		var newAgent = msc.getClusterAgent();
		current.agent = "mid.server." + newAgent;
}

function getMIDServerGr() {
		var agentName = current.agent;
		agentName = agentName.substring("mid.server.".length, agentName.length);

		var agentCache = new SNC.ECCAgentCache();
	var gr = agentCache.getByName(agentName);

		if (!gr)
				return;	 

		return gr;
}","abort_action":"false","filter_condition":"","condition":"MIDClusterManagementUtil.checkPrecondition(current)","name":"MID Server Cluster Management"}

 {"action_insert":"true","action_update":"true","action_delete":"false","action_query":"false","collection":"sys_hub_step_instance","advanced":"true","active":"true","when":"before","order":"100","add_message":"false","script":"(function executeRule(current, previous /*null when async*/) {
	// If Action cannot be updated, we cannot add or remove a Step, so abort
	if (!isActionSnapshot() && (isAddingToReadOnlyAction() || isRemovingFromReadOnlyAction())) {
		current.setAbortAction(true);
		gs.addErrorMessage("Cannot modify Steps for an Action " +
							 "with restrictive security or protection policy");
		return;
	}

	// If no Action is set, leave Step policy as specified
	if (gs.nil(current.action))
		return;

	// Otherwise, inherit sys_policy from Action
	current.sys_policy = current.action.sys_policy;

	function isActionSnapshot() {
		if (gs.nil(current.action))
			return false;

		return current.action.sys_class_name == "sys_hub_action_type_snapshot";
	}

	function isAddingToReadOnlyAction() {
		return !gs.nil(current.action) && !current.action.getRefRecord().canWrite();
	}

	function isRemovingFromReadOnlyAction() {
		return !gs.nil(previous.action) && !previous.action.getRefRecord().canWrite();
	}
})(current, previous);","abort_action":"false","filter_condition":"","condition":"current.action.changes()","name":"Step Protection Policy"}

