 {"action_insert":"false","action_update":"false","action_delete":"false","action_query":"false","collection":"","advanced":"true","active":"true","when":"action","order":"100","add_message":"false","script":"/**
 * This is the default escalation handler for on-call rotation escalations
 *
 * When it is called, the following variables are available:
 *
 *		current - the document that is associated with the escalation (such as an incident)
 *		escalation - the escalation record
 */

gs.include("OnCallRotation");

var eventName = escalation.event_name;
var rota = new OnCallRotation();

rota.whoIsNext(escalation);
while (rota.next()) {
	 // Generate an event for each pending escalation
	 if (rota.getType() == "device") {
			// Device notification
			gs.eventQueue(eventName + ".device", current, rota.getDevice(), rota.getPrimaryUser());
			gs.print("Generated Group Device Event for: " + rota.getDevice());
	 } else {
			// User notification
			gs.eventQueue(eventName + ".user", current, rota.getUser(), rota.getUserName());
			gs.print("Generated User Event for: " + rota.getUserName());
	 }
	 rota.addCurrentToNotified();
}
rota.continueEscalations(escalation);

","abort_action":"false","filter_condition":"","condition":"","name":"OnCallEscalation"}

 {"action_insert":"true","action_update":"false","action_delete":"false","action_query":"false","collection":"sn_hr_core_case","advanced":"true","active":"true","when":"before","order":"100","add_message":"false","script":"(function executeRule(current, previous /*null when async*/) {

	var hrServiceId = current.getValue('hr_service');
	if (hrServiceId) {
		var grService = new GlideRecord('sn_hr_core_service');
		if (grService.get(hrServiceId)) {
			var caseOptions = grService.getValue('case_options');
			if (caseOptions) {
				var options = {};
				var arr = caseOptions.split(',');
				for (var i = 0; i < arr.length; i++) {
					var grOption = new GlideRecord(hr.TABLE_SERVICE_OPTION);
					grOption.get(arr[i]);
					options[grOption.value + ""] = grOption.value + "";
				}

				// If not already passed in, and the case option isn't set, set the subject_person field to same value 
				// as opened_for

				if (!current.subject_person && !options.hasOwnProperty('DoNotDefaultSubjectPerson'))
					current.subject_person = current.opened_for;

				// Certain services add submitter's manager to watchlist
				if (options.hasOwnProperty('AddManagerToWatchlist')) {
					var grUser = new GlideRecord('sys_user');
					if (grUser.get(current.opened_for))
						current.watch_list += "," + grUser.manager;
				}

				// Certain services do not allow users to cancel their request
				if (options.hasOwnProperty('UserCannotCancel'))
					current.submitter_can_cancel = false;

				// Certain services don't want the auto assign logic to be invoked via the BR 
				if (options.hasOwnProperty('SkipAutoAssign'))
					current.skip_auto_assign = true;

				if (options.hasOwnProperty('SkipAutomaticUserAcceptanceState'))
					current.skip_automatic_user_acceptance_state = true;

				if (options.hasOwnProperty('AutomaticallyCreateDraftDocument'))
					current.auto_create_draft_document = true;

				if (options.hasOwnProperty('AutomaticallyInitiateDocumentTasks'))
					current.auto_initiate_doc_tasks = true;

			} else if (!current.subject_person) // no options exist, so going to default subject person
				current.subject_person = current.opened_for;
		}
	}

})(current, previous);","abort_action":"false","filter_condition":"","condition":"","name":"Apply Case Options"}

