 {"action_insert":"false","action_update":"false","action_delete":"false","action_query":"false","collection":"global","advanced":"true","active":"true","when":"after","order":"100","add_message":"false","script":"/**
 * Return the list of variables which are Choice fields in a conversation
 * 
 * We have to find the fields which are Choices so we can set up Decision
 * Outcomes based on the values the user selected for these choices.
 */
function sys_cb_decision_variableGetChoices() {

	// if we don't have current, we can't figure out the variable table
	if (typeof current == "undefined" || JSUtil.nil(current) || !current || JSUtil.nil(current.sys_id)) {
		gs.log("Error generating Topic choice-variable choices: current record is undefined or empty");
		return;
	}

	//answer should be automatically defined in our context when this function is called
	if (!answer) {
		gs.log("Error generating Topic choice-variable choices: answer is undefined");
		return;
	}
	var variable_table = current.getValue("variable_table");
	var variable_lookup = new TopicVariableAsChoice();

	var choices = variable_lookup.getChoices(variable_table);

	for (var i=0; i< choices.length; i++) {
		var choice = choices[i];
		answer.add(choice.value, choice.label);
	}	
}","abort_action":"false","filter_condition":"","condition":"","name":"sys_cb_decision_variableGetChoices"}

 {"action_insert":"true","action_update":"true","action_delete":"false","action_query":"false","collection":"sp_page","advanced":"true","active":"true","when":"before","order":"99","add_message":"false","script":"function onBefore(current, previous) {
	var id = current.id.toLowerCase().replace(/[^a-zA-Z0-9_]/g, '_');

	// Compact any multiple sequential underscores
	while (id.indexOf("__") != -1) 
		id = id.replace(/__/g, "_");

	// Replace any trailing underscores
	while (id != '' && id.charAt(id.length - 1) == '_') 
		id = id.substring(0, id.length - 1);

	if (current.id != id)
		current.id = id;
}","abort_action":"false","filter_condition":"","condition":"!current.id.nil()","name":"Page ID"}

