 {"action_insert":"true","action_update":"true","action_delete":"false","action_query":"false","collection":"item_option_new","advanced":"true","active":"true","when":"before","order":"100","add_message":"false","script":"(function executeRule(current, previous /*null when async*/) {
	var catItem = current.cat_item;
