 {"action_insert":"false","action_update":"true","action_delete":"false","action_query":"false","collection":"cmdb_ci_service_discovered","advanced":"true","active":"true","when":"before","order":"100","add_message":"false","script":"(function executeRule(current, previous /*null when async*/) {
