 {"action_insert":"false","action_update":"true","action_delete":"false","action_query":"false","collection":"change_request","advanced":"true","active":"true","when":"before","order":"100","add_message":"false","script":"(function executeRule(current, previous /*undefined when async or display*/) {

	// Preconditions for running conflict detection satisfied so return
	if (ChangeCheckConflicts.allowConflictDetection(current, previous))
		return;

	// Update conflict fields for current change_request record
	current.conflict_status = 'Not Run';
	current.conflict_last_run = "";

	// Build up a set of associated change_request records
	var map = {};
	var conflictGr = new GlideRecord('conflict');
	conflictGr.addQuery('change', current.getUniqueValue()).addOrCondition('conflicting_change', current.getUniqueValue());
	conflictGr.query();	
	while (conflictGr.next()) {
		map[conflictGr.change.toString()] = null;
		map[conflictGr.conflicting_change.toString()] = null;					
		conflictGr.deleteRecord();
	}
	delete map[current.getUniqueValue()];

	// Update conflict fields for associated change_request records
	for (var sysId in map) {
		conflictGr = new GlideRecord('conflict');
		conflictGr.addQuery('change', sysId).addOrCondition('conflicting_change', sysId);
		conflictGr.query();
		if (conflictGr.getRowCount() === 0) {
			var record = new GlideRecord('change_request');
			if (record.get(sysId)) {
				record.conflict_status = 'Not Run';
				record.conflict_last_run = '';				
				record.update();
			}
		}
	}

})(current, typeof previous != 'undefined' ?	previous : null);","abort_action":"false","filter_condition":"active=true^EQ","condition":"","name":"Clear Conflicts"}

