	var fixed = current.getValue('restricted_fields').split(',');
	var toCopy = current.getValue('fields_to_copy').split(',');
	var defaultValues = current.getValue('default_values').split(',');
	var hasErr = false;

	var common = checkIntersection(fixed, mandatory);
