 {"action_insert":"true","action_update":"true","action_delete":"false","action_query":"false","collection":"global","advanced":"true","active":"true","when":"after","order":"100","add_message":"false","script":"/**
 * create general events, tables need fields:
 * comments, assigned_to, active
 */
function global_events(current) {
	var name = current.sys_meta.name;
	global_simple_events(current);

	if (current.operation() != "insert" && current.comments.changes())
		gs.eventQueue(name + ".commented", current, gs.getUserID(), gs.getUserName());

	if (current.isValidField("assigned_to")) {
		if (!current.assigned_to.nil() && current.assigned_to.changes()) {
			gs.eventQueue(name + ".assigned", current, current.assigned_to.getDisplayValue(),
			previous.assigned_to.getDisplayValue());
		}
	}

	if (current.operation() != 'insert' && current.active.changesTo(false)) {
		// if incident_state is valid then use that, to avoid sending back a different value from previous release
		if (current.isValidField("incident_state"))
			gs.eventQueue(name + ".inactive", current, current.incident_state, previous.incident_state);
		else if (current.isValidField("state"))
			gs.eventQueue(name + ".inactive", current, current.state, previous.state);
	}
}

/**
 * do the inserted, updated events
 */
function global_simple_events(current) {
	var name = current.sys_meta.name;
	if (current.operation() == "insert")
		gs.eventQueue(name + ".inserted", current, gs.getUserID(), gs.getUserName());
	if (current.operation() == "update")
		gs.eventQueue(name + ".updated", current, gs.getUserID(), gs.getUserName());
}","abort_action":"false","filter_condition":"","condition":"","name":"global events"}

 {"action_insert":"true","action_update":"true","action_delete":"false","action_query":"false","collection":"pa_indicators","advanced":"true","active":"true","when":"after","order":"100","add_message":"false","script":"function onAfter(current, previous) {
	//This function will be automatically called when this rule is processed.
	var direction = current.direction;
	var direction_string = "not set";
	if (direction == 2 || direction == 3)
		direction_string = "to " + direction.getDisplayValue().toLowerCase();

	var indicator_source_name;
	var conditions;
	var facts_table;

	var rec = new GlideRecord("pa_cubes");
	if (rec.get(current.cube)) {
		indicator_source_name = rec.getValue("name");
		conditions = rec.getElement("conditions").getDisplayValue();
		facts_table = rec.getValue("facts_table");
	}

	var additional_conditions = current.conditions;

	var message = "{0} is measured {1} as unit {2}.\nThe goal for this indicator is {3}.\nIt is the {4} on data source {5}, which is using the table: \n{6}\nwith the following conditions:\n{7}";

	var parameters = [current.name, current.frequency.getDisplayValue().toLowerCase(), current.unit.getDisplayValue(), direction_string, current.aggregate.getDisplayValue().toLowerCase(), indicator_source_name, facts_table, conditions];

	if (additional_conditions) {
		message += "\nAdditional conditions:\n{8}";
		parameters.push(additional_conditions.getDisplayValue());
	}

	var description = gs.getMessage(message, parameters);

		// Fetch the indicator again to refresh the value of "id" and thereby to avoid UniqueConstraint error from DB
	var indicator = new GlideRecord(current.getTableName());
	if (indicator.get(current.sys_id)) {
		indicator.setValue('description', description);
		indicator.update();
