 {"action_insert":"true","action_update":"true","action_delete":"false","action_query":"false","collection":"cmdb_model_lifecycle","advanced":"true","active":"true","when":"before","order":"100","add_message":"false","script":"/*
Returns array of string of table names in hierarchy.
*/
function _getTableHierarchy(className) {
	var tableUtil = new TableUtils(className);
	var arrayUtils = new ArrayUtil();
	var tableArray = arrayUtils.convertArray(tableUtil.getTables());
	return tableArray.toString().split(',');
}

/*
Returns model based on mapped lifecycle table.
If no mapping is present returns cmdb_model.
*/
function _getLifecycleModel(lifecycleClass) {
	var lifecycleModelMap = {
		cmdb_model_lifecycle: 'cmdb_model',
		cmdb_hardware_model_lifecycle: 'cmdb_hardware_product_model',
		cmdb_consumable_model_lifecycle: 'cmdb_consumable_product_model',
		cmdb_application_model_lifecycle: 'cmdb_application_product_model',
		cmdb_contract_model_lifecycle: 'cmdb_contract_product_model',
		cmdb_facility_model_lifecycle: 'cmdb_facility_product_model',
		cmdb_service_model_lifecycle: 'cmdb_service_product_model'
	};
	var lifecycleClasses = Object.keys(lifecycleModelMap);
	var tableHierarchy = _getTableHierarchy(lifecycleClass);
	var lifecycleModel = 'cmdb_model';
	for (var i = 0; i < tableHierarchy.length; i++) {
		var tableName = tableHierarchy[i];
		if (lifecycleClasses.indexOf(tableName) > -1) {
			lifecycleModel = lifecycleModelMap[tableName];
			break;
		}
	}
	return lifecycleModel;
}

(function executeRule(current) {
	var SOFTWARE_MODEL = 'cmdb_software_product_model';
	var modelClass = current.model.sys_class_name.toString();
	var modelHierarchy = _getTableHierarchy(modelClass);
	var lifecycleClass = current.sys_class_name.toString();
	// Restrict creation of lifecycle for software model and their childs
	if (modelHierarchy.indexOf(SOFTWARE_MODEL) > -1) {
		gs.addErrorMessage(gs.getMessage('Lifecycle cannot be created for software model on "{0}"', [lifecycleClass]));
		current.setAbortAction(true);
	} else {
		var lifecycleModel = _getLifecycleModel(lifecycleClass);
		// Allow creation of lifecycle if model present in modelHierarchy
		if (modelHierarchy.indexOf(lifecycleModel) === -1) {
			gs.addErrorMessage(gs.getMessage('Lifecycle cannot be created for "{0}" on "{1}"', [modelClass, lifecycleClass]));
			current.setAbortAction(true);
		}
	}
})(current);","abort_action":"false","filter_condition":"","condition":"","name":"Verfiy model on lifecycle"}

 {"action_insert":"true","action_update":"true","action_delete":"false","action_query":"false","collection":"sys_number","advanced":"true","active":"true","when":"before","order":"100","add_message":"false","script":"/**
* Prevents creating sys_number records with colliding prefix:
*	- Same prefix
*	- Prefix ending with a number and same alphanumerical prefix as an existing record
*	- We get the alphanumerical prefix by selecting all characters up to the last non-digit one
*	- On the same table family, TASK and TASK67 collide, TASK and TASK3b5 don't
*/

function onBefore(current, previous) {
	var tableBase = new TableUtils(current.category).getAbsoluteBase();
	var tableFamily = new TableUtils(tableBase).getAllExtensions();
	tableFamily = tableFamily.toArray().join(',');
	//We trim the last digits
	var stemMatcher = /^(.*\D+)\d*$/i.exec(current.prefix);
	var stem = stemMatcher ? stemMatcher[1] : '';
	var potentialCollisions = getPotentialCollisions(stem, current.sys_id, tableFamily);
	while (potentialCollisions.next()) {
		if (isCollision(stem, potentialCollisions.prefix)) {
			msg = gs.getMessage('The prefix {0} collides with the prefix of an existing record', current.prefix);
			gs.addErrorMessage(msg);
			current.setAbortAction(true);
		}	
	}

	//Get potential collisions for the given stem
	//i.e records with prefix that starts with the same stem, other than the current one
	function getPotentialCollisions(stem, sys_id, tableFamily) {
		var numbers = new GlideRecord('sys_number');
		numbers.addQuery('prefix', 'STARTSWITH', stem);
		numbers.addQuery('sys_id', '!=', sys_id);
		numbers.addQuery('category', 'IN', tableFamily);
		numbers.query();
		return numbers;
	}

	//returns true if the prefix is a collision for the given stem
	//i.e if it is the stem followed by any number
	function isCollision(stem, prefix) {
		var re = new RegExp("^" + stem + "\\d*$", "i");
		return re.test(prefix);
	}
}","abort_action":"false","filter_condition":"prefixVALCHANGES^EQ","condition":"","name":"Prevent prefix collisions"}

