/**
 * onChange is used to set the location field based on the caller's location
 * @param {object} control - The HTML element that triggered the event
 * @param {string} oldValue - The previous value of the control
 * @param {string} newValue - The new value of the control
 * @param {boolean} isLoading - True if the form is loading, false if not
 */
function onChange(control, oldValue, newValue, isLoading) {
   if ((isLoading && !g_form.isNewRecord()) || (g_form.isLiveUpdating && g_form.isLiveUpdating()))
      return;

   if (newValue == '' || newValue == null) {
      g_form.setValue('location', '');
      return;
   }
   if (!g_form.hasField('location'))
      return;
   var caller = g_form.getReference('caller_id', setLocation);
}

/**
 * setLocation is used to set the location field based on the caller's location
 * @param {object} caller - The caller object that contains the location
 */
function setLocation(caller) {
   if (caller)
       g_form.setValue('location', caller.location);
}

