var GWC_HR_ButtonCondition_Utils = Class.create();
GWC_HR_ButtonCondition_Utils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    // -------- Query's --------

    _queryParamsMatrix: function(current, fieldName) {
        var paramsMatrixGR = new GlideRecord('x_gmeps_hr_parameters_matrix');

        if (current.hr_parameters != '') {
            paramsMatrixGR.get(current.hr_parameters);

            if (paramsMatrixGR.isValidRecord()) {
                return paramsMatrixGR.getValue(fieldName);
            }

        } else {
            paramsMatrixGR.addEncodedQuery('service=' + current.service_item + '^desire=' + current.desire);

            if (current.item != '') {
                paramsMatrixGR.addQuery('item', current.item);
            }

            if (current.subitem != '') {
                paramsMatrixGR.addQuery('subitem', current.subitem);
            }

            paramsMatrixGR.query();

            if (paramsMatrixGR.next()) {
                return paramsMatrixGR.getValue(fieldName);
            }
        }
    },

    // -------- Checks --------

    _checkItemApproval: function(current) {
        return (this._queryParamsMatrix(current, 'approval') == 'yes' ? true : false);
    },

