	test : function (text, result, expected){
		this.testNum++;
		var msg = " - test " + this.testNum + " ("+ text + ")";
		var details = "\nresult=" + result + "\nexpect=" + expected;

		var checkResult = false;

		if (Array.isArray(expected)){
			checkResult = expected.every(function(expectedElement){
				return (result.indexOf(expectedElement) > -1);
			}, this)
		} else {
			checkResult = (result == expected);
		}

		if (checkResult){
			this.testOK++;
			gs.info("🟢 OK" + msg );
		} else {
			this.testKO++;
			gs.info("🔴 KO" + msg + details);

