	/**
		@name getCompany
		@description Get the correct company (parent's company) for the given company's sys_id
		@param {String} [companyId] - Sysid of the company 
		@returns {String} Object contains the data segregation user's fields
	*/
	getCompany : function (companyId){
		try{
			// get the company GlideRecord
			var companyGr = new GlideRecord('core_company');
			if (companyGr.get(companyId)){
				if (!companyGr.parent.nil()){
					return this.getCompany(companyGr.parent);
				}
			}
			// if there is no match or if the parent is null, return the current company
			return companyId + "";
		} catch (exception) {
			gs.error ("Error in scmDataSegregationUtils - getCompany: " + JSON.stringify(exception));
		}
	},
