	/**
		@name triggerMaintainDSData
		@description Check if the BR to maintain Data Segregation data on the task should be triggered
		@param {GlideRecord(task)} [taskGr] - Task GlideRecord
		@returns {Boolean} True if the BR should trigger, false otherwise
	*/
	triggerMaintainDSData : function (taskGr){
		try{

			// get the related fields
			var relatedFieldsArr = this.getRelatedFields(taskGr);

			// check each field to know if it was updated
			var hasChanges = relatedFieldsArr.some(function(fieldPath){
				// take the "top" field, in case there is dot walking
				var mainFieldName = fieldPath.split(".").shift();

				// check if the field is changed
				if (taskGr[mainFieldName].changes()){
					return true;
				}
			}, this);

			return hasChanges;

		} catch (exception) {
			gs.error ("Error in scmDataSegregationUtils - triggerMaintainDSData: " + JSON.stringify(exception));
		}
	},
	/**
		@name maintainDSData
		@description Perform the maintenance of the Data Segregtion fields on the given task
		@param {GlideRecord(task)} [taskGr] - Task GlideRecord
	*/
	maintainDSData : function (taskGr){ 
		try{
			var dsNames = this.getDsNames();

			dsNames.forEach(function(dsName){
				var valueArr =  this.getRelatedValues(taskGr, dsName);
				var dsdc = this.findInArrayOfObject(this.dsdcArr, 'ds_name', dsName).shift();
				var datafield = dsdc.ds_data_field;
				if (valueArr.length > 0){
					var value = valueArr.shift();
					taskGr.setValue(datafield, value);
				} else {
					taskGr.setValue(datafield, '');
				}
			}, this);

		} catch (exception) {
			gs.error ("Error in scmDataSegregationUtils - maintainDSData: " + JSON.stringify(exception));
		}
	},

	/**
		@name getRelatedFields
		@description get the name/path of the fields for the given ds_name and task record, using the Data Segregation Data Config (DSDC). If no ds_name is given, return all the fields from all configs. If there is not config for the record's class, lookup the config for the parent's class, until a config is found
		@example For change Request and DS Name "company", returns ["requested_by.company","assignment_group.company"]
		@param {GlideRecord(task)} [taskGr] - Task GlideRecord
		@param {String} [dsName=] - data segregation config name
		@returns {Array} List of all the fields related to the Data Segregation 
	*/
	getRelatedFields : function (taskGr, dsName){
		try{			
			// Array to store the relevant config according the dsname
			var relevantDsDc = [];

			// if dsName not provided, get all the dsName from the config
			if (global.JSUtil.nil(dsName)){
				relevantDsDc = this.dsdcArr;
			} else /* if a ds name is provided, get only the relevant config*/ {
				relevantDsDc = this.findInArrayOfObject(this.dsdcArr, "ds_name", dsName);
			}

			// get the table hierachy
			var tableHierachyArr = j2js(new TableUtils(taskGr.getRecordClassName()).getHierarchy());

			// to store the results (related fields array)
			var relatedFieldsArr = [];

			// process all relevant configs
			relevantDsDc.forEach(function(config){
				tableHierachyArr.some(function (tableName){
					if (config.ds_value.hasOwnProperty(tableName)){
						// get the related fields from config
						var currentRelatedFieldsArr = config.ds_value[tableName];

						// concatenate to the main array
						relatedFieldsArr = this.ArrayUtil.concat(relatedFieldsArr, currentRelatedFieldsArr);

						// return true to stop iterating the table name hierachy
						return true;
					}
				}, this);
			}, this);

			return relatedFieldsArr;
		} catch (exception) {
			gs.error ("Error in scmDataSegregationUtils - getRelatedFields: " + JSON.stringify(exception));
		}
	},
	/**
		@name triggerUpdateTasksCompany
		@description Check if the BR to maintain  tasks related companies on tasks changed on the user should be triggered
		@param {GlideRecord(user)} [userGr] - User GlideRecord
		@param {GlideRecord(user)} [previousUserGr] - previous user GlideRecord (from the BR)
		@returns {Boolean} True if the BR should trigger, false otherwise
	*/
	triggerUpdateTasksCompany : function (userGr, previousUserGr){
		try{

			if (userGr.company.changes()){
				// get the parent companies of both the new and previous company
				var newParentCompany =  this.applyFunction(this.DS_COMPANY, userGr.company);
				var previousParentCompany =  this.applyFunction(this.DS_COMPANY, previousUserGr.company);

				// if the parent company is different, trigger the BR
				if (newParentCompany != previousParentCompany){
					return true;
				}
			}

			// in all other case, do not trigger the BR

			return false;

		} catch (exception) {
			gs.error ("Error in scmDataSegregationUtils - triggerUpdateTasksCompany: " + JSON.stringify(exception));
		}
	},
	/**
		@name getAllUserRelatedToCompany
		@description Get all the users related to the given comapny, including the manger (u_it_manager) 
		@param {GlideRecord(core_company)} [companyGr] - Company GlideRecord
		@returns {GlideRecord} GlideRecord with all the users related to the given company. Needs to perform .next() to get first element !
	*/
	getAllUserRelatedToCompany : function (companyGr){  //UGLY
		try{
			// get the user id
			var companyId = companyGr.getUniqueValue();		

			// query the task table with the query 
			var userGr = new GlideRecord('sys_user');
			var query = userGr.addQuery('company', companyGr.getUniqueValue());
			if (!companyGr.u_it_manager.nil()){
				query.addOrCondition('sys_id', companyGr.u_it_manager); 
			}
			userGr.query();

			return userGr;
		} catch (exception) {
			gs.error ("Error in scmDataSegregationUtils - getAllUserRelatedToCompany: " + JSON.stringify(exception));
		}
	},
	/**
		@name getAllTaskRelatedToUser
		@description Get all the tasks related to the given user, using the configuration ds_value
		@param {GlideRecord(sys_user)} [userGr] - user GlideRecord
		@returns {GlideRecord} GlideRecord with all the tasks related to the given user. Needs to perform .next() to get first element !
	*/
	getAllTaskRelatedToUser : function (userGr){ 
		try{
			// get the user id
			var userId = userGr.getUniqueValue();

			// build the query with each table in the userPath object
			var queryElement = [];
			for (var table in this.userPath){
				queryElement.push("ref_" + table +"." + this.userPath[table] + "=" + userId);
			};

			var queryStr = queryElement.join("^OR");

			// query the task table with the query 
			var taskGr = new GlideRecord('task');
			taskGr.addEncodedQuery(queryStr);
			taskGr.query();

			return taskGr;
		} catch (exception) {
			gs.error ("Error in scmDataSegregationUtils - getAllTaskRelatedToUser: " + JSON.stringify(exception));
		}
	},
	/**
		@name getRelatedValues
		@description get the values of the related fields for the given ds_name and task record, using the Data Segregation Data Config (DSDC). Using getRelatedFields() to get the related fields.
		@example for Change Request: takes the values stored in requrested_by.company and in assignment_group.company on the task record.
		@param {GlideRecord(task)} [taskGr] - Task GlideRecord
		@param {String} [dsName] - data segregation config name
		@returns {Array} List of all the value from the fields related to the Data Segregation 
	*/
	getRelatedValues : function (taskGr, dsName){
		try{
			// to store the results (related fields values array) 
			var relatedValuesArr = [];

			// make sure a dsName is provided
			if (global.JSUtil.notNil(dsName)){
				var relatedFieldsArr = this.getRelatedFields(taskGr, dsName);

				// get the values for each field
				relatedFieldsArr.forEach(function(relatedField){

					var fieldValue = taskGr.getElement(relatedField);
					if (global.JSUtil.notNil(fieldValue)){
						fieldValue = this.applyFunction(dsName, fieldValue);

						relatedValuesArr.push(fieldValue);
					}
				}, this);
			}

			return relatedValuesArr;

		} catch (exception) {
			gs.error ("Error in scmDataSegregationUtils - getRelatedValues: " + JSON.stringify(exception));
		}
	},
