	/**
		@name getUserRelatedValues
		@description get the values of the related fields for the given ds_name and user record, using the Data Segregation Data Config (DSDC).
		@param {GlideRecord(sys_user)} [userId] - User GlideRecord
		@param {String} [dsName] - data segregation config name
		@returns {Array} List of all the values from the fields related to the Data Segregation 
	*/
	getUserRelatedValues : function (userId, dsName){
		try{



			// to store the results (related fields values array) 
			var userRelatedValuesArr = [];

			// get cached value, if any
			var cacheKey = ["userRelatedValues", dsName, userId].join("_");
			if (gs.getProperty('scm.ds.enable_user_data_cache', "true") == "true"){
				var cachedData = this.getCachedVariable(cacheKey, gs.getProperty('scm.ds.user_data_cache_ttl', null));
				if (JSUtil.notNil(cachedData)){
					if (gs.getProperty('scm.ds.showFilterQuery', "false") == "true") {
						if (gs.hasRole('admin') || GlideImpersonate().isImpersonating()){
							gs.info("using cached filter with key " + cacheKey + " : " + cachedData);
						}
					}
					userRelatedValuesArr = JSON.parse(cachedData);
					return userRelatedValuesArr;
				}
			}

			// get user
			var userGr = new GlideRecord('sys_user');
			userGr.get(userId);


			// make sure a dsName is provided
			if (global.JSUtil.notNil(dsName)){
				var userFieldsArr = this.getUserRelatedFields(dsName);

				// get the values for each field
				userFieldsArr.forEach(function(relatedField){

					// split the value, as it may come from a List field, and process each entry separatly
					var fieldValueArr = userGr.getElement(relatedField).split(",") ;
					fieldValueArr.forEach(function (value){
						if (global.JSUtil.notNil(value)){
							var fieldValue = this.applyFunction(dsName, value);

							userRelatedValuesArr.push(fieldValue);
						}
					}, this);
				}, this);
			}

			// Make sure values are uniques and return
			userRelatedValuesArr = this.ArrayUtil.unique(userRelatedValuesArr);


			if (gs.getProperty('scm.ds.enable_user_data_cache', "true") == "true"){
				this.setCachedVariable(cacheKey, JSON.stringify(userRelatedValuesArr));
			}
			return userRelatedValuesArr;

		} catch (exception) {
			gs.error ("Error in scmDataSegregationUtils - getUserRelatedValues: " + JSON.stringify(exception));
		}
	},
	/**
		@name applyFunction
		@description get the function from the given DSDC config name and apply it to the input value
		@param {String} [dsName] - data segregation config name
		@param {String} [inputValue] - Input value on which to apply the function
		@returns {String}  output value, after applying the function
	*/
	applyFunction : function (dsName, inputValue){
		try{
			// default outpout value in the input value
			var outputValue = inputValue;

			// make sure a dsName is provided
			if (global.JSUtil.notNil(dsName)){
				// get the config, to get the function from ds_apply_function
				var dsdcArr = this.findInArrayOfObject(this.dsdcArr, 'ds_name', dsName);
				var config = dsdcArr.shift();
				if (global.JSUtil.notNil(config)){
					var funcName = config.ds_apply_function;
					if (global.JSUtil.notNil(funcName)){
						outputValue = this[funcName](inputValue);
					}
				}
			}
			return outputValue;
		} catch (exception) {
			gs.error ("Error in scmDataSegregationUtils - applyFunction: " + JSON.stringify(exception));
		}
	},

	/**
		@name getCompaniesManagedByUser
		@description Get all the companies manager by the given user
		@param {String} [userId] - User sys_id
		@returns {Array} List of companies the user is managing
	*/
	getCompaniesManagedByUser : function (userId){
		try{
			// array to store the company the user is manager for
			var companyArr = [];

			// query all the companies where the user is manager
			var companyGr = new GlideRecord('core_company');
			companyGr.addQuery('u_it_manager', 'CONTAINS', userId);
			companyGr.addNotNullQuery('u_it_manager');
			companyGr.orderBy('name');
			companyGr.query();

			while (companyGr.next()){
				companyArr.push(companyGr.getUniqueValue());
			}

			return companyArr;

		} catch (exception) {
			gs.error ("Error in scmDataSegregationUtils - getCompaniesManagedByUser: " + JSON.stringify(exception));
		}
	},
	/**
		@name getRelatedCompaniesForUser
		@description Get all the related companies for the given user (from the company field and all the companies the user is manager). Applies the function ds_apply_function on the result
		@param {GlideRecord(sys_user)} [userGr] - Task GlideRecord
		@returns {Array}  List of related companies for the given user
	*/
	getRelatedCompaniesForUser: function (userGr){
		try{
			// get the company I am manager for 
			var userCompaniesArr = this.getCompaniesManagedByUser(userGr.getUniqueValue());

			// add the company from the user record
			if (!userGr.company.nil()){
				userCompaniesArr.push(userGr.getValue('company'));
			}

			// to store the parent companies
			var userParentCompanies = [];

			// apply the function on all companies retrived above
			userCompaniesArr.forEach(function (company){
				userParentCompanies.push(this.applyFunction(this.DS_COMPANY, company));
			}, this);

			// make sure it is unique and return
			userParentCompanies = this.ArrayUtil.unique(userParentCompanies);

			return userParentCompanies;


		} catch (exception) {
			gs.error ("Error in scmDataSegregationUtils - getRelatedCompaniesForUser: " + JSON.stringify(exception));
		}
	},
