	/**
		@name triggerDataSegregationFilter
		@description Check if the BR to filter the task with  Data Segregation should be triggered
		@param {GlideRecord(task)} [taskGr] - Task GlideRecord
		@returns {Boolean}  True if the BR should trigger, false otherwise
	*/
	triggerDataSegregationFilter : function (taskGr){
		try{


			// check is enabled 
			if (gs.getProperty('scm.ds.enable_query_filter', false) == "false" && !this.isTestUser()){
				return false;
			}


			/* 
			   is not interactive session and not from a mobile, return
			   note: from mobile apps, isInteractive returns false, that why we need also to check isMobile. See KB0749141
			*/
			if (!gs.isInteractive() && !gs.isMobile()){
				return false;
			}

			// Check if the user has at least on the the override role
			var whiteListRolesStr = gs.getProperty('scm.ds.whitelist_roles', '');
			if (JSUtil.notNil(whiteListRolesStr) && !this.isTestUser()){
				var whiteListRoles = whiteListRolesStr.split(",");
				for (var i=0; i < whiteListRoles.length; i++){
					// if has an override role, do not run the BR
					if (gs.hasRole(whiteListRoles[i])){
						return false;
					}
				}
			}

			if (gs.getProperty('scm.ds.approverRead_enable', "false") == 'true'){ 
				// If from approval, do not apply Data Segragation (for backend)
				var canApproversRead = new global.ApproverUtils().canApproversRead();
				if (canApproversRead){
					return false;
				}
			}

			if (gs.getProperty('scm.ds.whitelist_url_enable', "false") == 'true'){
				// if using the specific widget in portal, do not apply data segregation (
				if (gs.isInteractive()){
					var currentURL = gs.action.getGlideURI().toString(); // Get current URL
					var whiteListeURL = gs.getProperty('scm.ds.whitelist_url', ''); // Get whitelisted URL // 

					if (JSUtil.notNil(whiteListeURL)){
						// check all whitelisted url against current url to see if it march
						var whiteListURLArr = whiteListeURL.split(",");

						var isWhiteListedURL = whiteListURLArr.some(function(url){
							if (currentURL.indexOf(url) > -1){
								return true;
							}
						},this);

						// if whitelisted url matched, skip data segregation
						if (isWhiteListedURL){
							return false;
						}
					}
				}
			}

			// in all other cases, run the BR
			return true;
		} catch (exception) {
			gs.error ("Error in scmDataSegregationUtils - triggerDataSegregationFilter: " + JSON.stringify(exception));
		}
	},


	/**
		@name setFilter
		@description Get the filter for filtering the task with the Data Segregation
		@param {GlideRecord(task)} [taskGr] - Task GlideRecord
		@param {String} [userId] - User GlideRecord
		@returns {String}  Filter string (encoded query)
	*/
	setFilter : function (taskGr, userId){
		try{ 
			// save task query before building the filter (for info/debug purpose)
			var taskBeforeQuery  = taskGr.getEncodedQuery();
			var beforeQueryOperator = (JSUtil.nil(taskBeforeQuery)? "": "^");

			// variable that holds the complete query string
			var queryString = "";

			// temp array to build the fitlers
			var filterElements = [];

			// to store the name of all the data fields present in the configurations
			var dataFields = [];

			// get and process all the configs
			this.dsdcArr.forEach(function(dsdc){ 
				// add values and data field to filter elements
				filterElements.push (
					{
						ds_data_field : dsdc.ds_data_field,
						ds_values: this.getUserRelatedValues(userId, dsdc.ds_name )
					}
				);
				// store the data field name
				dataFields.push(dsdc.ds_data_field);
			}, this);


			// if enabled, allow to see record with all data fields empty
			if (gs.getProperty('scm.ds.show_records_with_empty_data_fields', "true") == "true"){
				var showRecEmptyDataFieldsArr = [];
				dataFields.forEach(function(dataField){ 
					showRecEmptyDataFieldsArr.push(dataField + "ISEMPTY");
				},this);
				if (JSUtil.notNil(showRecEmptyDataFieldsArr)){
					queryString += (queryString == "" ? "": "^NQ" ) +  showRecEmptyDataFieldsArr.join("^")  + beforeQueryOperator + taskBeforeQuery;
				}
			}
			// build the filter from the filter elements gathered
			var queryFilterArr= [];

			filterElements.forEach(function(element){
				var valuesArr = element.ds_values;
				valuesArr.forEach(function(val){
					queryFilterArr.push(element.ds_data_field+ "=" + val);
				}, this);
			}, this);

			if (JSUtil.notNil(queryFilterArr)){
				queryString += (queryString == "" ? "": "^NQ" ) +  queryFilterArr.join("^OR") + beforeQueryOperator + taskBeforeQuery;
			}

			// add filter for the user fields, like watch list, etc.
			if (gs.getProperty('scm.ds.add_query_on_user_fields', "true") == "true"){
				// get the list of user fields for the record class
				var userFieldsArr = this.getUserFields(taskGr);

				// build the fitlers
				var userFilterArr = [];

				userFieldsArr.forEach(function(fieldName){
					if (taskGr.isValidField(fieldName)){
						userFilterArr.push(fieldName+ "LIKE" + gs.getUserID());
					}
				});

				// add filter to the query string
				if (JSUtil.notNil(userFilterArr)){
					queryString += (queryString == "" ? "": "^NQ" )  + userFilterArr.join("^OR") +  beforeQueryOperator + taskBeforeQuery;
				}
			}
			// add query to task

			taskGr.addEncodedQuery(queryString);
			var taskAfterQuery = taskGr.getEncodedQuery();

			// show message, fs any
			if (gs.getProperty('scm.ds.showFilterQuery', "false") == "true") {
				if (gs.hasRole('admin') || GlideImpersonate().isImpersonating() || this.isTestUser()){
					var msg = [];
					msg.push("user=" + gs.getUserDisplayName() + "("+ gs.getUserID()+ ")");
					msg.push("Before=" + taskBeforeQuery);
					msg.push("After =" + taskAfterQuery);
					msg.push("Query =" + queryString);
					gs.info("DS info:\n" + msg.join("\n"));
				}
			}

		} catch (exception) {
			gs.error ("Error in scmDataSegregationUtils - setFilter: " + JSON.stringify(exception));
		}
	},
	/**
		@name getUserFields
		@description  Get the name of the fields that should contains the user sys_id using the Data Segregation Data Config (DSDC). Also lookup forthe config for the parent's class (incident would have the field from the incident itself and the one from the task, etc…)
		@example for fields like watch_list, etc...
		@param {GlideRecord(task)} [taskGr] - Task GlideRecord
		@returns {Array} List of all the fields related to the users
	*/
	getUserFields : function (taskGr){
		try{

			// get the table hierachy
			var tableHierachyArr = j2js(new TableUtils(taskGr.getRecordClassName()).getHierarchy());

			// to store the results (user fields array)
			var userFieldsArr = [];

			// process all relevant configs
			tableHierachyArr.forEach(function (tableName){
				if (this.dsUserField.hasOwnProperty(tableName)){
					// get the related fields from config
					var currentUserFieldsArr = this.dsUserField[tableName];

					// concatenate to the main array
					userFieldsArr = this.ArrayUtil.concat(userFieldsArr, currentUserFieldsArr);
				}
			}, this);

			return userFieldsArr;

		} catch (exception) {
			gs.error ("Error in scmDataSegregationUtils - getUserFields: " + JSON.stringify(exception));
		}
	},
	/**
	 @name findInArrayOfObject
	 @description find all objects in an array by one of the attribute value. Necessary because Array.find is currently not supported
	 @param {Array} [arrayOfObject] - Array of object
	 @param {String} [fieldName] - Field name in the object
	 @param {String} [fieldValue] - Value in the field
	 @returns {Object} return an Array of matching object
	 */
	findInArrayOfObject : function (arrayOfObject, fieldName, fieldValue){
		try{ 
			if (global.JSUtil.nil(arrayOfObject)){
				return [];
			}

			var matchingArr = [];
			arrayOfObject.some(function (element){
				if (element[fieldName] == fieldValue){
					matchingArr.push(element);
					return true;
				}
			});
			return matchingArr;
		} catch (exception) {
			gs.error ("Error in script include 'VDGSyncTools' - findInArrayOfObject: " + JSON.stringify(exception));
		}
	},
	/**
		@name getCachedVariable
		@description get the variable from the cache and check it is not expired
		@param {String} [variableName] - Name of the variable
		@param {integer} [ttl=60000] - Time to live in ms. To check how long is the cache valid. Default is 10 minutes
		@returns {String} Value cached or undefined if not found or expired
	*/
	getCachedVariable : function (variableName, ttl){
		try{
			// check if cache should be ignored
			if (this.noCache){
				return;
			}

			// default ttl
			ttl = ttl || 600000;

			// build variable names
			var variableNameTime = variableName + ".time";
			var variableNameData = variableName + ".data";

			// get variables values
			var storedTime =  gs.getSession().getClientData(variableNameTime);

			// if nothing stored, return
			if (global.JSUtil.nil(storedTime)){
				return;
			}

			// get current time
			var nowTime = parseInt(new Date().getTime());

			// calculate time difference between cached value and ttl 
			var timeDiff = nowTime - parseInt(storedTime);

			// if cache expired, return
			if (timeDiff > ttl){
				return;
			}

			// get stored data and return it
			var storedData = gs.getSession().getClientData(variableNameData);
			return storedData;

		} catch (exception) {
			gs.error ("Error in scmDataSegregationUtils - getCachedVariable: " + JSON.stringify(exception));
		}
	},
	/**
		@name setCachedVariable
		@description store variable in cache
		@param {String} [variableName] - Name of the variable
		@param {String} [variableData] - Data to store
	*/
	setCachedVariable : function (variableName, variableData){
		try{
			// build variable names
			var variableNameTime = variableName + ".time";
			var variableNameData = variableName + ".data";

			// get current time
			var nowTime = new Date().getTime();

			// set data in cache
			gs.getSession().putClientData(variableNameData, variableData);
			gs.getSession().putClientData(variableNameTime, nowTime);

		} catch (exception) {
			gs.error ("Error in scmDataSegregationUtils - scmDataSegregationUtils: " + JSON.stringify(exception));
		}
	},

	type: 'scmDataSegregationUtils'
};
