(function executeRule(current, previous /*null when async*/ ) {
    var desc = current.getValue('description');
    var node1 = current.getValue('node');
    var Addional_information = current.getValue('additional_info');
    var parseValue = JSON.parse(Addional_information);
    var fields = parseValue.additional_content.split(',::');
    var node_fqdn = trim(fields[0].split(':')[1]);
    var ip_addr = trim(fields[1].split(':')[1]);
    var group = trim(fields[2].split(':')[1]);
    var inc_priority = trim(fields[4].split(':')[1]);
    var email = fields[5].split(':')[1];
    var pager = trim(fields[6].split(':')[1]);
    var discp = desc.split(';;');
    var title1 = trim(discp[0]);
    var alrtmsg = trim(discp[1]);
    alrtmsg = alrtmsg.replace(/::/g, "\n");
    var custom_msg = parseValue.em_custom_msg;
    var em_missing_ci = parseValue.missing_ci_default;
    var inc_category = trim(fields[7].split(':')[1]);
    var inc_subcategory = trim(fields[8].split(':')[1]);
    if ((!current.cmdb_ci) && (em_missing_ci))
        current.cmdb_ci.setDisplayValue(em_missing_ci);



    if (pager.slice(-1) == '}')
        pager = pager.substring(0, pager.length - 1);

    if (inc_subcategory.slice(-1) == '}')
        inc_subcategory = inc_subcategory.substring(0, inc_subcategory.length - 1);
    if (!title1)
        title1 = desc;

    if (custom_msg)
        title1 = custom_msg + title1;

    if (!ip_addr)
        ip_addr = current.cmdb_ci.ip_address;

    if (!node_fqdn)
        node_fqdn = ' ';


    current.short_description = title1;
    current.description = 'Node: ' + current.getValue('node') + "\n" + 'IP Address: ' + ip_addr + "\n" + 'Node FQDN: ' + node_fqdn + "\n" + 'Message Key: ' + current.message_key + "\n" + title1 + "\n" + alrtmsg;
    //

    // send email without incident	
    if (email != 'Unknown' && email != '' && inc_priority == 'Unknown') {
        gs.eventQueue("EM_Alert_email", current, email);
    }



    // call pager function			
    if (pager != 'Unknown' && pager != 'Unknown}' && pager != '') {
        Sendpage(pager, title1);
    }




    //Incident
    var inc, grp, calr_id, calr, work_stn, work_s, inc_sys_id, usrname, o_name, o_name_id, ci_loc, locname, loc_sys_id;

    if (inc_priority != 'Unknown' && group != 'Unknown' && group != 'error') {

        inc = new GlideRecord('incident');
        inc.initialize();
        inc.short_description = title1;
        inc.description = current.description;
        inc.impact = this.getImpact(inc_priority);
        inc.urgency = this.getUrgency(inc_priority);

        if (!group) {
            current.description = "Assignment group is NULL defaulting to TCS-SystemsMonitoring  ::     Event message : " + inc.description;
            inc.description = current.description;
            group = 'TCS-SystemsMonitoring';
        }



        if (inc_priority == 2 || inc_priority == 1) {
            group = 'Major Incident Management';
        }

        //gs.addInfoMessage(group);
        grp = this.getAssignmentGroup(group);

        // if assignment group missing
        if (!grp) {
            current.description = "Assignment group : " + group + "  does not exists defaulting to TCS-SystemsMonitoring   ::     Event message : " + inc.description;
            inc.description = current.description;
            group = 'TCS-SystemsMonitoring';
            grp = this.getAssignmentGroup(group);
        }

        inc.assignment_group = grp;

        // caller
        calr_id = 'EM_SPECTRUM.RCC';
        calr = this.getUsr(calr_id);
        inc.u_caller_contact_number = '000-000-0000';

        // if caller ID missing

        if (!calr) {

            current.description = "Caller : " + calr_id + " does not exists in servicenow   ::     Event message : " + inc.description;
            inc.description = current.description;

        }

        inc.caller_id = calr;



        //constant field  for EM and same sys id has to be maintained across all instances




        o_name = 'Monitoring Automation';
        o_name_id = this.getUsr(o_name);

        // if caller ID missing

        if (!o_name_id) {

            current.description = "Opened User : " + o_name + " does not exists in servicenow   ::     Event message : " + inc.description;
            inc.description = current.description;

        }


        inc.opened_by = o_name_id;


        //constant field  for EM and same sys id has to be maintained across all instances

        //	inc.opened_by='cc589779db4d0b80caf3d411ce9619ac';

        if (inc_category) {

            if (inc_category == 'app_std') {
                inc.category = 'software';
                inc.subcategory = 'error_message';

            } else {
                inc.category = inc_category;
                inc.subcategory = inc_subcategory;
            }
        }


        inc.contact_type = 'automation';
        inc.u_n_a = true;




        locname = 'Delta Enterprise';
