function getImpact(priority) {

    var impact = 3;
    if (priority == 1) {
        impact = 1;
    } else if (priority == 2) {
        impact = 2;
    } else if (priority == 3) {
        impact = 2;
    } else if (priority == 4) {
        impact = 2;
    } else if (priority == 5) {
        impact = 3;
    }
    return impact;
}

function getUrgency(priority) {
    var urgency = 3;
    if (priority == 1) {
        urgency = 1;
    } else if (priority == 2) {
        urgency = 1;
    } else if (priority == 3) {
        urgency = 2;
    } else if (priority == 4) {
        urgency = 3;
    } else if (priority == 5) {
        urgency = 3;
    }
    return urgency;
}

function getAssignmentGroup(grp_name) {
    var grp_sys_id;
    var grGroup = new GlideRecord('sys_user_group');
    grGroup.addActiveQuery();
    grGroup.addQuery('name', grp_name);
    grGroup.query();
    if (grGroup.next()) {
        grp_sys_id = grGroup.sys_id;
    }

    return grp_sys_id;
}



function getUsr(usrname) {
    var usr_sys_id;
    var gusr = new GlideRecord('sys_user');
    gusr.addActiveQuery();
    gusr.addQuery('name', usrname);
    gusr.query();
    if (gusr.next()) {
        usr_sys_id = gusr.sys_id;
    }
    return usr_sys_id;
}


function getLoc(locname) {
    var loc_sys_id;
    var lname = new GlideRecord('cmn_location');
    lname.addActiveQuery();
    lname.addQuery('name', locname);
    lname.query();
    if (lname.next()) {
        loc_sys_id = lname.sys_id;
    }
    return loc_sys_id;
}




