		if (grCostCenter.u_company_code.nil()) return this.error(task, 'AribaTask.createHardwareOrder(task): Cost Center (' + grCostCenter.code.getDisplayValue() + ') has no Company Code.');

		//Check there are items to order
		var grItems = new GlideRecord('sc_req_item');
		grItems.addQuery('request', task.u_request.getValue());
		grItems.addQuery('cat_item.model.u_supplier_id', task.u_supplier_id.getValue());
		grItems.query();

		//If there are no items or item, throw an error
		if (grItems.getRowCount() == 0) {
			return this.error(task, 'AribaTask.createHardwareOrder(task): ' + task.u_request.getDisplayValue() + ' has no items for supplier "' + task.u_supplier_id.getDisplayValue() + '".');
		}

		//Create the order
		var id = task.number.getDisplayValue();
		if (gs.getProperty('instance_name') !== 'adobe') {
			var opened = new GlideDateTime(task.opened_at);
			id += '-' + opened.getNumericValue();
		}

		this.createOrder({
			ref_id: id,
			business_justification: 'Other',
			//TODO: SN and Business Use should only be passed in for orders containing a computer that requires them
			serial_number: 'WSS Order',
			replacement_type: 'Primary Machine',
			//Preparer is either the person who opened the request, or the same user incase a request doesn't exist
			preparer: task.u_request.opened_by.email.getDisplayValue() || task.u_user.email.getDisplayValue(),
			requester: task.u_user.email.getDisplayValue(),
			title: 'Workspace Store Order: ' + id,
			company_code: grCostCenter.u_company_code.getDisplayValue(),
			low_po_balance_warning: 'false',
			new_data_exchange: 'No',
			store: this.getStoreName(task),
			po_type: 'Fixed Invoice PO'
		});

		var gl, grModel;
		var shipTo = this.generateShipTo(task, grCostCenter);

		if(!shipTo) return this.error(task, 'AribaTask.createHardwareOrder(task, grCostCenter): Ship To could not be generated Task ' + task.getDisplayValue());

		while (grItems.next()) {

			grModel = grItems.cat_item.model.getRefRecord();
			// force it to a non-catalog order for the non visible ariba furniture catalog
			var nonCatItem = grModel.getTableName() === 'cmdb_facility_product_model';

			this.addItem({
				model: grModel,
				company_code: grCostCenter.u_company_code.getDisplayValue(),
				cost_center: grCostCenter.code.getDisplayValue(),
				general_ledger: grModel.u_gl_code,
				external_comment: metaData.notes,
				reporting_field: task.u_user.email.getDisplayValue() + ' / ' + task.u_request.u_contact_information.getDisplayValue(),
				ship_to: shipTo,
				non_cat_item: nonCatItem
				//category: 'M'
			});
			//add bundled items to the order
			if (grModel.bundle) {
				var additionalItem;
				var grCmdbM2mModelComponent = new GlideRecord('cmdb_m2m_model_component');
				grCmdbM2mModelComponent.addQuery("parent", grModel.sys_id);
				grCmdbM2mModelComponent.query();
				while (grCmdbM2mModelComponent.next()) {
					additionalItem = grCmdbM2mModelComponent.child.getRefRecord();
					if (additionalItem.isValidRecord()) { // << only perform operations on it if it's a valid record
						this.addItem({
							model: additionalItem,
							company_code: grCostCenter.u_company_code.getDisplayValue(),
							cost_center: grCostCenter.code.getDisplayValue(),
							general_ledger: additionalItem.u_gl_code,
							external_comment: metaData.notes,
							reporting_field: task.u_user.email.getDisplayValue() + ' / ' + task.u_request.u_contact_information.getDisplayValue(),
							ship_to: shipTo,
							non_cat_item: nonCatItem
							//category: 'M'
						});
					}
				}
			}
		}

		return this.getOrder();
	},

	createSoftwareOrder: function (task, isUpdate) {
		if (task.u_model.isNil()) return this.error(task, 'AribaTask.createSoftwareOrder(task): Missing Software Model');
		if (task.u_user.isNil()) return this.error(task, 'AribaTask.createSoftwareOrder(task): Missing Ship To User');

		var id = task.number.getDisplayValue();

		this.createOrder({
			ref_id: id,
			business_justification: 'Other',
			preparer: task.u_model.u_adobe_owner.email.getDisplayValue(),
			requester: task.u_model.u_adobe_owner.email.getDisplayValue(),
			title: 'Workspace Store Order: ' + id,
			company_code: 'ADUS',
			low_po_balance_warning: 'false',
			new_data_exchange: 'No',
			store: 'WSS Software',
			po_type: 'Fixed Invoice PO'
		});

		/*
		For services, which Software is, there has to be a timeline for when the services start and when they end.
		This is more important for someone performing a service. For software it is less important.

		The date MUST be in the future.
		The date MUST not be in the past when it's purchased.
		We've been guided to provide good information. We will not be penalized for not being perfect.
		3 violations of paying for services previously rendered will cause an action.
		*/
		var now = new Date();
		var startDate = new Date(now.getTime() + (14 * 24 * 60 * 60 * 1000));
		var endDate;

		//Does the description contain the word 'month'
		if (task.u_model.short_description.getDisplayValue().toLowerCase().indexOf('month') !== -1) {
			endDate = new Date(startDate.getTime() + (31 * 24 * 60 * 60 * 1000));
		} else {
			endDate = new Date(startDate.getTime() + (365 * 24 * 60 * 60 * 1000));
		}

		this.addItem({
			model: task.u_model.getRefRecord(),
			company_code: 'ADUS',
			cost_center: this._software_cost_center,
			general_ledger: this._software_general_ledger,
			external_comment: '',
			deliver_to: task.u_user.getDisplayValue(), //Do not use this field in PRs with multiple line items.
			reporting_field: task.u_user.email.getDisplayValue(),
			ship_to: '0024',
			start_date: startDate.toISOString(),
			end_date: endDate.toISOString(),
			category: 'Z'
		});

		return this.getOrder();
	},

	//Identify the type of request this is.
	_getType: function (task) {
		//Tasks can either be an REQ or a Model.
		//Software orders are tackled individually, and should be a Model
		//Hardware is tackled by supplier, and should be an REQ

		//If it's an REQ, order all the necessary items in the REQ
		var request = task.u_request.getRefRecord();

		//If it's a Model, check the type and create the correct order.
		var model = task.u_model.getRefRecord();

		//TODO: How do I tell if this request is a Hardware Request?
		//      For now I'll try to just not make Software sc_requests
		if (request.isValidRecord())
			return this.TYPE.HARDWARE;
		else if (model.isValidRecord() && model.getTableName() == 'cmdb_software_product_model')
			return this.TYPE.SOFTWARE;

		return this.error(task, 'AribaTask._getType(task): TYPE could not be identified for Ariba Task ' + task.getDisplayValue());
	},

	order: function (task) {
		try {

			var soapOrder = null;

			switch (this._getType(task)) {
				case this.TYPE.HARDWARE: soapOrder = this.createHardwareOrder(task); break;
				case this.TYPE.SOFTWARE: soapOrder = this.createSoftwareOrder(task); break;
			}

			if (typeof soapOrder === 'object') {
				this.startRuntime();
				var response = soapOrder.execute();
				var responseBody = response.getBody();
				var status = response.getStatusCode();
				task.work_notes.setJournalEntry('Ariba response:\t(' + this.getRuntime().getByFormat('HH:mm:ss.SSS') + ')\n\nStatus Code: ' + status + '\n\nBody:\n' + responseBody);
				task.work_notes.setJournalEntry(
					'Ariba called with:\n\n' +
					soapOrder.getRequestBody()
						.replace(/(<urn:CustomString name="OptionalReportingField">)([\s\S]*?)(<\/urn:CustomString>)/g, '$1<OptionalReportingField - hidden for privacy requirements>$3')
						.replace(/(<urn:DeliverTo>)([\s\S]*?)(<\/urn:DeliverTo>)/g, '$1<delivery address - hidden for privacy requirements>$3')
				);

				//If successful
				if (responseBody.includes('StatusString')) {
					var xmlDoc = new XMLDocument2();
					xmlDoc.parseXML(responseBody);
					var statusString = xmlDoc.getNodeText('//StatusString');
					task.setValue('u_status', statusString);
					task.setValue('u_purchase_request', xmlDoc.getNodeText('//UniqueName'));

					if (['Submitted', 'Ordered', 'Ordering', 'Receiving', 'Received', 'Approved'].indexOf(statusString) !== -1) {
						//Thumbs up!
						task.update();
					} else if (statusString == 'Composing') {
						this.error(task, 'Entry is stuck in Composing. I should probably do something more than just adding this comment');
					} else if (statusString != 'Failed') {
						this.error(task, 'Ariba Status was "' + statusString + '". Script Include "AribaTask" was not configured to handle this status');
					}
				} else {//If fails
					task.setValue('u_status', 'Failed');
					this.error(task);
				}
			} else {
				this.error(task, 'AribaTask.order(task): Could not identify what needed to be ordered');
			}

		} catch (ex) {
			this.error(task, 'Exception Error: \n\n' + JSON.stringify(ex, null, 2));
		}
	},


	updateAllOpenOrders: function () {
		var grAribaTasks = new GlideRecord('u_ariba_task');
		grAribaTasks.addNotNullQuery('u_purchase_request');
		//ignore error status as of now to capture issues after tagging required for assets 
		//may need to be refined when we get into trouble when api-call count gets to high
		//grAribaTasks.addQuery('u_error', 'false');
		grAribaTasks.addQuery('u_status', 'NOT IN', ['Completed', 'Cancelled', 'Canceling', 'Denied', 'Failed' ,'Invalid']);
		grAribaTasks.addQuery('state', '1'); //Open
		grAribaTasks.query();

		var batch = [];

		while (grAribaTasks.next()) {

			batch.push({
				sys_id: grAribaTasks.sys_id.getDisplayValue(),
				id: grAribaTasks.u_purchase_request.getDisplayValue(),
				status: grAribaTasks.u_status.getDisplayValue(),
				type: this._getType(grAribaTasks)
			});

			if (batch.length === 40) {
				this.updatePurchaseRequest(batch);
				batch = [];
			}
		}

		if (batch.length) {
			this.updatePurchaseRequest(batch);
		}
	},

	createNewOrder: function (current) {
		current.u_status = 'Processing';
		current.update();
		this.order(current);
	},

	createAllNewOrders: function () {
		var grAribaTasks = new GlideRecord('u_ariba_task');
		grAribaTasks.addQuery('u_purchase_request', '');
		grAribaTasks.addQuery('u_status', 'New');
		grAribaTasks.addQuery('state', '1'); //Open
		grAribaTasks.addQuery('u_error', 'false');
		grAribaTasks.query();

		while (grAribaTasks.next()) {
			grAribaTasks.u_status = 'Processing';
			grAribaTasks.update();
			this.order(grAribaTasks);
		}
	},

	makeMePreparer: function (task) {
		try {

			var soapOrder = null;

			switch (this._getType(task)) {
				case this.TYPE.HARDWARE: soapOrder = this.createHardwareOrder(task, true); break;
				case this.TYPE.SOFTWARE: soapOrder = this.createSoftwareOrder(task, true); break;
			}

			this.changeOperation('Update');
			soapOrder.setStringParameter('preparer', gs.getUser().getEmail());

			if (soapOrder) {
				var response = soapOrder.execute();
				var responseBody = response.getBody();
				var status = response.getStatusCode();

				gs.addInfoMessage('Status: ' + status + '\n\nResponse Body:' + responseBody);

				return status;
			}
		} catch (ex) {
			this.error(task, 'Exception Error: \n\n' + JSON.stringify(ex, null, 2));
		}

		return null;

	},

	revertAll: function (task) {
		try {

			var soapOrder = null;

			switch (this._getType(task)) {
				case this.TYPE.HARDWARE: soapOrder = this.createHardwareOrder(task, true); break;
				case this.TYPE.SOFTWARE: soapOrder = this.createSoftwareOrder(task, true); break;
			}

			this.changeOperation('Update');

			if (soapOrder) {
				var response = soapOrder.execute();
				var responseBody = response.getBody();
				var status = response.getStatusCode();

				gs.addInfoMessage('Status: ' + status + '\n\nResponse Body:' + responseBody);

				return status;
			}
		} catch (ex) {
			this.error(task, 'Exception Error: \n\n' + JSON.stringify(ex, null, 2));
		}

		return null;

	},

	error: function (task, error, setError) {
		task.u_error = setError !== false;
		if (error) task.work_notes.setJournalEntry(error);
		return task.update();
	},

	type: 'AribaTask'
};

