function onClick(g_form) {
    showFields().then(
        process
    );

    function showFields() {
        return g_modal.showFields({
            title: 'Enter Comment and Reversal Document',
            fields: [{
                    type: 'string',
                    name: 'comment',
                    label: 'Reason:',
                    mandatory: true
                },
                {
                    type: 'string',
                    name: 'reversal_document',
                    label: 'Reversal Document Number:',
                    mandatory: true
                }
            ],
            size: 'lg'
        });
    }

    function process(fieldValues) {
        var comment = fieldValues.updatedFields[0].value;
        var rd = fieldValues.updatedFields[1].value;

        var patternCorrelation = /^\d{9,10}$/; //check exactly 10 digits
        var isValidCorrelation = patternCorrelation.test(rd.trim());


        if (isValidCorrelation) {
            g_form.setValue('reversal_document', rd.trim());
            g_form.setValue('comments', comment);
            g_form.setValue('state', "4");
            g_form.save();
        } else {
            alert('Please add the correct 9 or 10 digit number from the ERP system');
            showFields()
                .then(process);

        }
    }
}
