/// <returns>The assignments in a list of Assignment objects.</returns>
public async Task<List<Assignment>> GetAssignments(Course course)
{
    //create client
    var client = new HttpClient();

    //get web request
    var uri = new Uri(webAPI + "courses/" + course.id + "/assignments?per_page=1000");

    //submit request
    string json = await client.GetStringAsync(uri);

