var ADBSmartTrackBusinessRuleScript = Class.create();
ADBSmartTrackBusinessRuleScript.prototype = {
    initialize: function() {},


    getManufacturer: function(current, previous) {

        var manufacturerSKU = current.u_manufacturer_sku.getDisplayValue();
        var incidentDesc = 'Smart Track ArticleID not Found for Manufacturer SKU : ' + manufacturerSKU;
        gs.info("manufacturerSKU from BR: " + manufacturerSKU);

        var imstkn = this.getIMSToken();
        var transactionID = this.getTransactionID(imstkn);


        var articleID = "";

        //Trying to get articke id for three times
        for (var i = 0; i < 3; i++) {





            articleID = this.getArticleID(imstkn, transactionID, manufacturerSKU);



            //SW1 SKU (AAA-03850 & AAA-03579) could have multiple SKUs in SmartTrack catalog depending on the version
            //	so if there is no match with direct SKU we need to call SmartTrack API with the parameter to add "_" + version
            if (articleID == 'articleID_Null') {

                manufacturerSKU = manufacturerSKU + '_' + current.version.getDisplayValue();
                articleID = this.getArticleID(imstkn, transactionID, manufacturerSKU);
                gs.info("SmartTrack: " + articleID + " manufacturerSKU: " + manufacturerSKU);
                if (articleID == 'articleID_Null') {
                    gs.info("1SmartTrack: articleID not found using verison ", "inci");
                } else
                    break;
            } else
                break;
        }

        if (articleID == 'articleID_Null') {


            // this.createIncident(incidentDesc);



        } else {
            gs.info("1SmartTrack: articleID found");
            var productID = this.getProductUseRights(imstkn, transactionID, articleID, manufacturerSKU);
            var importID = this.getProducts(imstkn, transactionID, productID, manufacturerSKU);
        }

        gs.info("SmartTrack : @@@@articleID:" + articleID + " transactionID:" + transactionID + " productID:" + productID + " importID: " + importID);
    },


    //get_articleID
    getArticleID: function(imstkn, transactionID, manufacturerSKU) {
        var xmlDoc = new XMLDocument2();
        gs.info("SmartTrack: get_articleID: function execution is gettting started");
        var get_art = new sn_ws.SOAPMessageV2('ADB:Smarttrack', 'get_articles');
        get_art.setRequestHeader('Authorization', imstkn);
        get_art.setRequestHeader('api_key', gs.getProperty("ADBSmartTrackAPIkey"));
        get_art.setStringParameterNoEscape('tid', transactionID);
        get_art.setStringParameterNoEscape('man_art_no', manufacturerSKU);

        var resp_getArticles = get_art.execute();
        var responseBody_getArticles = resp_getArticles.getBody();
        var Status_getArticles = resp_getArticles.getStatusCode();
        gs.info("SmartTrack: soap message for get_article have been executed and returned code: " + Status_getArticles + " manufacturerSKU:" + manufacturerSKU);

        if (Status_getArticles == 200) {

            xmlDoc.parseXML(responseBody_getArticles);
            var param1 = xmlDoc.getFirstNode("//article_id");

            if (param1 != null) {

                var articleID = param1.getTextContent();

                var softModel = new GlideRecord("cmdb_software_product_model");
                softModel.addQuery('u_manufacturer_sku', manufacturerSKU);
                softModel.query();

                while (softModel.next()) {
                    //gs.info("@@@@SmartTrack: manufacturerSKU"+ manufacturerSKU + " ART: "+articleID);
                    softModel.u_smarttrack_article_id = articleID;
                    softModel.update();
                    gs.info("SmartTrack: updated the articleID: " + articleID + " to the respective  manufacturerSKU:" + manufacturerSKU);
                    return articleID;
                }

            } else {
                gs.info("articalID is null for manufacturerSKU: " + manufacturerSKU);
                return "articleID_Null";

            }

        } else {
            gs.info("Failed to retrieve Article ID");
            return "articleID_failed";


        }
    },


    //get_product_use_rights
    getProductUseRights: function(imstkn, transactionID, articleID, manufacturerSKU) {

        var xmlDoc = new XMLDocument2();
        gs.info("SmartTrack: get_product_use_rights: function execution is gettting started");
        var get_prdUseRt = new sn_ws.SOAPMessageV2('ADB:Smarttrack', 'get_product_use_rights');
        get_prdUseRt.setRequestHeader('Authorization', imstkn);
        get_prdUseRt.setRequestHeader('api_key', gs.getProperty("ADBSmartTrackAPIkey"));
        get_prdUseRt.setStringParameterNoEscape('tid', transactionID);
        get_prdUseRt.setStringParameterNoEscape('article_id', articleID);

        var resp_prdUseRt = get_prdUseRt.execute();
        var responseBody_prdUseRt = resp_prdUseRt.getBody();
        var Status_prdUseRt = resp_prdUseRt.getStatusCode();
        gs.info("SmartTrack: soap message for get_product_use_rights have been executed and returned code: " + Status_prdUseRt + " articleID:" + articleID);

        if (Status_prdUseRt == 200) {

            xmlDoc.parseXML(responseBody_prdUseRt);
            var param2 = xmlDoc.getFirstNode("//product_id");

            gs.info("smarttrack:" + param2);


            var productID = param2.getTextContent();

            var softModel = new GlideRecord("cmdb_software_product_model");
            softModel.addQuery('u_manufacturer_sku', manufacturerSKU);
            softModel.query();

            while (softModel.next()) {
                softModel.u_smarttrack_product_id = productID;
                softModel.update();
                gs.info("smarttrack: updated the product_id: " + productID + " to the respective manufacturerSKU:" + manufacturerSKU);

                return productID;
            }
        } else {
            gs.info("Failed to retrieve product_id");
            return "productID_failed";
        }

    },

    //get_products
    getProducts: function(imstkn, transactionID, productID, manufacturerSKU) {

        var xmlDoc = new XMLDocument2();
        gs.info("SmartTrack: get_products: function execution is gettting started");
        var get_products = new sn_ws.SOAPMessageV2('ADB:Smarttrack', 'get_products');
        get_products.setRequestHeader('Authorization', imstkn);
        get_products.setRequestHeader('api_key', gs.getProperty("ADBSmartTrackAPIkey"));
        get_products.setStringParameterNoEscape('tid', transactionID);
        get_products.setStringParameterNoEscape('product_id', productID);

        var resp_products = get_products.execute();
        var responseBody_products = resp_products.getBody();
        var Status_products = resp_products.getStatusCode();
        gs.info("Smarttrack: soap message for get_products have been executed and returned code: " + Status_products + "productID" + productID);

        if (Status_products == 200) {

            xmlDoc.parseXML(responseBody_products);
            var param3 = xmlDoc.getFirstNode("//import_id");


            var importID = param3.getTextContent();

            var softModel = new GlideRecord("cmdb_software_product_model");
            softModel.addQuery('u_manufacturer_sku', manufacturerSKU);
            softModel.query();

            while (softModel.next()) {

                softModel.u_smarttrack_import_product_id = importID;
                softModel.update();
                gs.info("smarttrack: updated the import_id: " + importID + " to the respective manufacturerSKU" + manufacturerSKU);
                return importID;
            }
        } else {
            gs.info("Failed to retrieve importID");
            return "importID_failed";
        }


    },


    //get Transaction ID for One time use
    getTransactionID: function(imstkn) {

        var xmlDoc = new XMLDocument2();
        gs.info("SmartTrack: getTransactionID: function execution is gettting started");
        var re = new sn_ws.SOAPMessageV2('ADB:Smarttrack', 'start_transaction');
        re.setRequestHeader('authorization', imstkn);
        re.setRequestHeader('api_key', gs.getProperty("ADBSmartTrackAPIkey"));
        re.setStringParameterNoEscape('login', gs.getProperty("ADBSmartTrackLogin"));
        re.setStringParameterNoEscape('password', gs.getProperty("ADBSmartTrackPwd"));
        response = re.execute();
        responseBody = response.getBody();
        httpStatus = response.getStatusCode();
        gs.info("SmartTrack: soap message for transaction id have been executed and returned code: " + httpStatus);

        if (httpStatus == 200) {
            xmlDoc.parseXML(responseBody);
            var param = xmlDoc.getFirstNode("//return");
            var transactionID = param.getTextContent();
            gs.info("SmartTrack: " + transactionID);

            return transactionID;

        } else {
            gs.info("Failed to retrieve Transaction ID");
        }
    },

    //get IMS token	
    getIMSToken: function() {

        var imstkn = 0;
        var parser = new global.JSONParser();
        gs.info("SmartTrack: getIMSToken: function execution is gettting started");
        var r = new sn_ws.RESTMessageV2('ADB:SmartTrack', 'get_IMSToken');
        var response = r.execute();
        var responseBody = response.getBody();
        var httpStatus = response.getStatusCode();
        gs.info("SmartTrack: getIMSToken: rest message for IMS have been executed and returned code:" + httpStatus);
        if (httpStatus == 200) {
            gs.info("SmartTrack: getIMSToken: assigning variable for imstkn value");
            var oauthObject = parser.parse(responseBody);
            imstkn = oauthObject.access_token;

            return imstkn;
        } else {
            gs.info("Failed to retrieve IMS token");
        }

    },

    //create Incident consolidated
    createIncident: function() {
        var template = '01- Smart Track ArticleID not Found';
        var desc = "Smart Track ArticleID not found for below Manufacturer SKUs :\n";
        var proModel = new GlideRecord('cmdb_software_product_model');
        proModel.addQuery('u_manufacturer_skuISNOTEMPTY^u_smarttrack_article_idISEMPTY');
        proModel.query();
        while (proModel.next()) {
            desc += proModel.getValue('u_manufacturer_sku') + "\n";
        }


        var incident = new GlideRecord("incident");
        incident.addEncodedQuery("active=true^state!=6^short_description=Smart Track ArticleID not Found");
        incident.query();
        if (!incident.hasNext()) {
            incident.initialize();
            incident.description = desc;
            incident.applyTemplate(template);
            incident.caller_id = gs.getProperty('adb.nestware.user');
            incident.insert();
        }
    },

    type: 'ADBSmartTrackBusinessRuleScript'
};

