		getAndGenerateContent();
	});

}

function createAssessment(asmt_type, app_sys_id){

	var worknotes = $j('#wn'+ asmt_type).val();
	$j('.asmt a').attr('disabled', true);
	$j('.apm-loading').show();
	restCall('asmt_custom_processor.do?operation=createassessmentinstance&app_sys_id=' + app_sys_id + '&asmt_type=' + asmt_type +'&worknotes=' + worknotes).done(function (res) {
        generateContent(res);
		getAndGenerateContent();
	});
}

function generateApmScores(){
	$j('.asmt a').attr('disabled', true);
	$j('.apm-loading').show();
	restCall('asmt_custom_processor.do?operation=generateapmscores').done(function (res) {
        $j('#rspns').html(res.msg).addClass(res.cssclass);
		$j('.asmt a').attr('disabled', false);
		$j('.apm-loading').hide();
	});

}


function generateContent(res){   
    var outp = "";
	if (!res.status) {
		outp += '<strong>Record Condition Not Met</strong><br />';
	}

	// outp += '<strong>brs</strong><br />'+JSON.stringify(res);
    for (var asmt_type in res.asmt_metric_type){

        outp += "<div class='asmt'><h2>" + res.asmt_metric_type[asmt_type].name + 
        " <a href='nav_to.do?uri=asmt_metric_type.do?sys_id=" +
        asmt_type.toString() +
        "' target='_blank'><span class='icon icon-open-document-new-tab'></span></a> <span class='icon apm-loading icon-loading'></span></h2>" +

        generateCategoryDetails(res.asmt_metric_type[asmt_type].asmt_metric_category) +  "<br />" +

		generateAssesmentDetails(res.asmt_metric_type[asmt_type].asmt_assessment_instance, asmt_type) + 

		generateAssesmentFooter(asmt_type, app_sys_id ,
								res.asmt_metric_type[asmt_type].asmt_assessment_instance.readyCnt) + 

		"</div>";

    }

	outp += addFooter();

    $j('#asmt_content').html(outp);

}


function generateAssesmentDetails(asmt_instance, asmt_type){
    var outp = "<table class='table table-striped'><thead><tr><th>Instance</th><th>Assigned to</th><th>Created</th><th>State</th><th>Action</th></tr><tbody>";
    for (var instance in asmt_instance){  
		if (instance == 'readyCnt') continue;
        outp += "<tr><td><a href='asmt_assessment_instance.do?sys_id=" +  asmt_instance[instance].sys_id + "' target='asmt'>"+ asmt_instance[instance].number +"</a><a href='assessment_take2.do?sysparm_assessable_sysid=" +  asmt_instance[instance].sys_id + "&sysparm_reader_view=true' target='asmt_pv'><span class='icon icon-preview' title='Show results'></span></a></td><td>"+ asmt_instance[instance].user.displayValue +  
		"</td><td>"+ asmt_instance[instance].sys_created_on.displayValue +  
		"</td><td>"+ asmt_instance[instance].state.displayValue;
		if (asmt_instance[instance].state.value == 'complete') {
			outp += "<p><strong>" + Math.ceil(asmt_instance[instance].result.total) +" / " + Math.ceil(asmt_instance[instance].result.max) + "</strong></p>";
		} else {
			outp += "</td><td>" + generateButton(asmt_instance[instance].sys_id, asmt_type, asmt_instance[instance].user.isAssignee, asmt_instance[instance].state.value);
		}
		outp += "</td></tr>"; 
    }
    outp += "</tbody></table>";
    return outp;

}

function generateAssesmentFooter(asmt_type,app_sys_id,readyCnt){

	var outp = "";
	if (!readyCnt) {
    //  outp +=   "<textarea id='wn"+ asmt_type +"' style='margin-bottom:10px; overflow: hidden; word-wrap: break-word; resize: none; height: 40px;' class='form-control' placeholder='Reason for new Assessment' ></textarea>";
	 outp += "<a href='javascript:createAssessment(\"" + asmt_type + "\", \"" + app_sys_id + "\")' class='btn btn-info btn-sm '><span class='icon icon-add-circle'></span> Create new Assessment</a>";
	}

	return outp;


}

function generateCategoryDetails(asmt_category){
	var typeMax = 0;
    var outp = "<div class='categ'>Categories:</div>";
    for (var category in asmt_category){ 
        outp += "<div class='categ'>&#10687; " 
        + asmt_category[category].name + "<span style='font-size:9pt; color:#81878e'> - Weight: "+ asmt_category[category].weight +" | Questions: "+ 
        asmt_category[category].total_metrics +" | Category Max: "+asmt_category[category].max_score +"</span></div>";  
		typeMax += Number(asmt_category[category].max_score);
    }
	outp += "<div> Total Max Score: <strong>" + typeMax + "</strong></div>"; 
    outp += "";
    return outp;

}

