// date for the selected date range
(function executeRule(current, previous) {
    // condition current.date.changes()
    if (!current.date.nil()) {
        var query = new GlideQuery('incidents');
        query.addQuery('active', 'true');
        query.addQuery('date_closed', '<=', current.date);
        query.addQuery('date_closed', '>=', current.date.addDays(-6));
        if (current.provider_auth != null) {
            var e = new GlideEncryptionUtil();
            var authToken = e.getClearPassword2('token_verification', 'token', current.provider_auth.outbound_message_creation);
            if (JSUtil.notNil(authToken))
                query.setRequestHeader('Authorization', 'Bearer ' + authToken);
        }
        query.query();
        while (query.next()) {
            var owner = query.getValue('owner');
            var date = query.getValue('date_closed');
            // do not add if the owner is already in the list
            if (!current.owner_grouped_by_date.contains(owner + '~' + date)) {
                current.owner_grouped_by_date.add(owner + '~' + date);
            }
        }
    }
})(current, previous);

