 function sendMessage(current) {
     try {
         var nrDetails = current.correlation_id.split("_");
         var nrAccountId = nrDetails[0];
         var destinationId = nrDetails[1];
         var records = new GlideRecord('api_key_credentials');
         records.addQuery('name', 'nr_' + destinationId);
         records.query();
         var nrIntegrtionKey = "";
         var nrEnv = "";
         var apiKeyDetails = "";
         while (records.next()) {
             apiKeyDetails = records.api_key.split("_");
         }
         if (apiKeyDetails == null || apiKeyDetails == "") {
             gs.debug("nrIntegrtionKey was not found");
             return; /* nrIntegrtionKey was not found */
         }
         nrIntegrtionKey = apiKeyDetails[0];
         nrEnv = apiKeyDetails[1];
         if (nrIntegrtionKey == null || nrIntegrtionKey == "" || nrEnv == null || nrEnv == "") {
             gs.debug("corrupted apiKeyDetails");
             return; /* corrupted apiKeyDetails */
         }
         var nrSyncService = "";
         switch (nrEnv) {
             case "us":
                 nrSyncService = "service";
                 break;
             case "eu":
                 nrSyncService = "service.eu";
                 break;
             case "st":
                 nrSyncService = "staging-service";
                 break;
             default:
                 gs.debug("not supported NR env for sync");
                 return;
         }
         var callbackRequest = new sn_ws.RESTMessageV2('New Relic Sync API', 'Post');
         callbackRequest.setStringParameterNoEscape('nrSyncService', nrSyncService);
         callbackRequest.setStringParameterNoEscape('accountId', nrAccountId);
         callbackRequest.setStringParameterNoEscape('destinationId', destinationId);
         callbackRequest.setStringParameterNoEscape('incidentId', current.number);
         callbackRequest.setStringParameterNoEscape('stateId', current.state);
         callbackRequest.setRequestHeader('Integration-Key', nrIntegrtionKey);
         callbackRequest.setRequestHeader('content-type', 'application/json');
         callbackRequest.execute();
     } catch (ex) {
         gs.addErrorMessage("failed New Relic Sync API: " + ex.message);
     }
 }
