  initialize: function () {},
  /** This function retrieves the total percentage value from all segment records
   * @param project - project SysID
   * @return segmentTotal - total (int) percentage
   */
  getSegmentTotal: function (project) {
    var segmentTotal = 0;
    var segments = new GlideRecord('u_resourcemgmt_segments');
    segments.addQuery('u_project', project);
    segments.query();

    while (segments.next()) {
      segmentTotal += segments.u_percentage;
    }
    return segmentTotal;
  },
  /** This function sets the total percentage value on the project record
   * @param project - project SysID
   * @param total - total value to set as segment total
   */
  setSegmentTotal: function (project, total) {
    var proj = new GlideRecord('pm_project');
    proj.get(project);
    proj.u_segment_total = total;
    proj.update();
  },
  /** This function calls the getSegmentTotal function to retrieve current aggregates, and calls the setSegmentTotal to update the associated project.
   * @param project - project SysID
   */
  calcSegmentTotal: function (project) {
    var total = this.getSegmentTotal(project);
    this.setSegmentTotal(project, total);
  },
  /** This function checks for duplicate segments (excludes current segment record)
 	*@param sys_id - current segment SysID
 	* @param project - project SysID
	@param segment - the segment to be checked against.
 	* @param total - total value to set as segment total
 	*@return boolean - true = duplicate records exist.
 	*/
  lookupDuplicateSegments: function (sys_id, project, segment) {
    var duplicate = new GlideRecord('u_resourcemgmt_segments');
    duplicate.addQuery('u_project', project);
    duplicate.addQuery('u_segment', segment);
    duplicate.addQuery('sys_id', '!=', sys_id);
    duplicate.query();

    if (duplicate.next()) {
      return true;
    }

    return false;
  },

	clearPlannedDates: function (sysID, table) {
		switch (table) {
			case 'pm_project':
			case 'pm_program':
				var grPmProject = new GlideRecord(table);
				if (grPmProject.get(sysID)) {
					grPmProject.setDisplayValue('start_date', '');
					grPmProject.setDisplayValue('end_date', '');
					//grPmProject.setWorkflow(false);
					grPmProject.setValue("run_calc_brs", false);
					grPmProject.update();
				}
				break;

			default:
				gs.log('The table ' + table + ' is not supported.', 'ADBITBMUtils');
				break;
		}
	},

  /** This function checks if jiraKey of the Portfolio exists in CloudTech portfolios property (adb.itbm.portfolio.ct_themes)
   *@param jiraKey - Jira key
   *@return boolean: true if it exists in the system property.
   */
  checkCTJira: function(jiraKey) {
		return gs.getProperty('adb.itbm.portfolio.ct_themes').indexOf(jiraKey) !== -1  ? true : false;
  },

  /** This function copies data when Demand tranforms to Project
   *@param demandSysId - demand sys_id
   *@param projectSysId - project sys_id
   *@return object: project sys_id, number and label.
   */
  copyProjectData: function(demandSysId, projectSysId) {
        var dmn2prjHelper = new DemandToProjectCreationHelper();
        var projectTable = "pm_project";
        var projNumber, tableDisplayName;
        var demand = new GlideRecord('dmn_demand');
        demand.addQuery('sys_id', demandSysId);
        demand.query();

        if (demand.next()) {
            var project = new GlideRecord("pm_project");
            if(project.get(projectSysId)) {
                var fields = ['short_description', 'primary_program', 'business_case', 'sys_domain', 'investment_class', 'investment_type', 'risk_of_performing', 'risk_of_not_performing', 'enablers', 'barriers', 'in_scope', 'out_of_scope', 'assumptions', 'goals', 'strategic_objectives', 'business_unit', 'department', 'impacted_business_units', 'business_applications', 'business_capabilities',
'cmdb_ci', 'company', 'priority', 'urgency', 'impact', 'rate_model', 'project_manager', 'project_currency'];
                for (var index in fields) {
                    var field = fields[index];
                    if (project.isValidField(field)) {
						project.setValue(field, demand.getValue(field));
                    }
                }
				if(!project.getValue('primary_portfolio')) {
					project.setValue("primary_portfolio", demand.getValue("portfolio"));
				}
                project.setValue("demand", demandSysId);
                project.setValue("calculation_type", 'manual');

                var resp = dmn2prjHelper._setProjectDatesAndDurationFromDemand(project, demand, projectTable);
                if (resp.status == 'error')
                    return {
                        'sysId': null,
                        'number': '',
                        'label': 'Project',
                        'error': resp.message
                    };


                project.update();
            }
            if (JSUtil.nil(projectSysId)) {
                var allErrorMessages = j2js(gs.getErrorMessages());
                if (JSUtil.notNil(allErrorMessages) && allErrorMessages.length > 0)
                    return {
                        'sys_id': projectSysId,
                        'number': '',
                        'label': 'Project',
                        'error': allErrorMessages[0]
                    };
                else
                    return {
                        'sys_id': projectSysId,
                        'number': '',
                        'label': 'Project'
                    };
            }


            GlideSysAttachment.copy(demand.sys_class_name, demandSysId, projectTable, projectSysId);
            projNumber = project.getValue('number');
            tableDisplayName = project.getClassDisplayValue();

            if (JSUtil.nil(demand.related_records))
                demand.related_records = projectSysId;
            else
                demand.related_records = demand.related_records + "," + projectSysId;
            demand.project = projectSysId;
            demand.state = (demand.close_demand == 'on_creating_project') ? '9' : '8';
            demand.stage = 'project';
            demand.update();
        }

        return {
            'sys_id': projectSysId,
            'number': projNumber,
            'label': tableDisplayName
        };
    },

  type: 'ADBITBMUtils',
};

