	createSoftwareOrder: function (task, isUpdate) {
		if (task.u_model.isNil()) return this.error(task, 'AribaTask.createSoftwareOrder(task): Missing Software Model');
		if (task.u_user.isNil()) return this.error(task, 'AribaTask.createSoftwareOrder(task): Missing Ship To User');

		var id = task.number.getDisplayValue();

		this.createOrder({
			ref_id: id,
			business_justification: 'Other',
			preparer: task.u_model.u_adobe_owner.email.getDisplayValue(),
			requester: task.u_model.u_adobe_owner.email.getDisplayValue(),
			title: 'Workspace Store Order: ' + id,
			company_code: 'ADUS',
			low_po_balance_warning: 'false',
			new_data_exchange: 'No',
			store: 'WSS Software',
			po_type: 'Fixed Invoice PO'
		});

		/*
		For services, which Software is, there has to be a timeline for when the services start and when they end.
		This is more important for someone performing a service. For software it is less important.

		The date MUST be in the future.
		The date MUST not be in the past when it's purchased.
		We've been guided to provide good information. We will not be penalized for not being perfect.
		3 violations of paying for services previously rendered will cause an action.
		*/
		var now = new Date();
		var startDate = new Date(now.getTime() + (14 * 24 * 60 * 60 * 1000));
		var endDate;

		//Does the description contain the word 'month'
		if (task.u_model.short_description.getDisplayValue().toLowerCase().indexOf('month') !== -1) {
			endDate = new Date(startDate.getTime() + (31 * 24 * 60 * 60 * 1000));
		} else {
			endDate = new Date(startDate.getTime() + (365 * 24 * 60 * 60 * 1000));
		}

