			return this.TYPE.SOFTWARE;

		return this.error(task, 'AribaTask._getType(task): TYPE could not be identified for Ariba Task ' + task.getDisplayValue());
	},

	order: function (task) {
		try {

			var soapOrder = null;

			switch (this._getType(task)) {
				case this.TYPE.HARDWARE: soapOrder = this.createHardwareOrder(task); break;
				case this.TYPE.SOFTWARE: soapOrder = this.createSoftwareOrder(task); break;
			}

			if (typeof soapOrder === 'object') {
				this.startRuntime();
				var response = soapOrder.execute();
				var responseBody = response.getBody();
				var status = response.getStatusCode();
				task.work_notes.setJournalEntry('Ariba response:\t(' + this.getRuntime().getByFormat('HH:mm:ss.SSS') + ')\n\nStatus Code: ' + status + '\n\nBody:\n' + responseBody);
