						.replace(/(<urn:CustomString name="OptionalReportingField">)([\s\S]*?)(<\/urn:CustomString>)/g, '$1<OptionalReportingField - hidden for privacy requirements>$3')
						.replace(/(<urn:DeliverTo>)([\s\S]*?)(<\/urn:DeliverTo>)/g, '$1<delivery address - hidden for privacy requirements>$3')
				);

				//If successful
				if (responseBody.includes('StatusString')) {
					var xmlDoc = new XMLDocument2();
					xmlDoc.parseXML(responseBody);
					var statusString = xmlDoc.getNodeText('//StatusString');
					task.setValue('u_status', statusString);
					task.setValue('u_purchase_request', xmlDoc.getNodeText('//UniqueName'));

					if (['Submitted', 'Ordered', 'Ordering', 'Receiving', 'Received', 'Approved'].indexOf(statusString) !== -1) {
						//Thumbs up!
						task.update();
					} else if (statusString == 'Composing') {
						this.error(task, 'Entry is stuck in Composing. I should probably do something more than just adding this comment');
					} else if (statusString != 'Failed') {
						this.error(task, 'Ariba Status was "' + statusString + '". Script Include "AribaTask" was not configured to handle this status');
					}
				} else {//If fails
					task.setValue('u_status', 'Failed');
					this.error(task);
				}
			} else {
				this.error(task, 'AribaTask.order(task): Could not identify what needed to be ordered');
			}

		} catch (ex) {
			this.error(task, 'Exception Error: \n\n' + JSON.stringify(ex, null, 2));
		}
	},


	updateAllOpenOrders: function () {
